public void init()
{
    Object      caller;
    Args        callerArgs;
    xSession    xSession = new xSession();
    interCompanyInventAvailabilityFormCache = new InterCompanyInventAvailabilityFormCache();
    // <GEERU>
    #ISOCountryRegionCodes
    // </GEERU>

    showCompany = true;

    if (!element.args().record())
    {
        caller      = element.args().caller();
        callerArgs  = caller.args();

        if (!callerArgs.record())
        {
            throw error("@SYS4205");
        }
    }
    else
    {
        if (element.args().record().TableId == tableNum(InventSum))
        {
            callerInventSum = element.args().record();
        }
    }

    this.initUnitConverters();
    session               = xSession.sessionId();

    super();

    element.design().caption(strFmt("@SYS93875",TradeInterCompany::inventTable(element.args().record()).ItemId));

    element.updateDesign(InventDimFormDesignUpdate::Init);

    whsInventReserveQtyMap = new Map(Types::String, Types::Class);

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        valueSecCurRU.visible(true);
        costPrice_RU.visible(true);
    }
    // </GEERU>
}

/// <summary>
///     Gets a <c>WHSInventReserveQty</c> instance for the given <c>InventSum</c> record.
/// </summary>
/// <param name="_inventSum">
///     <c>InventSum</c> record supplying values for non WHS specific fields.
/// </param>
/// <returns>
///     <c>WHSInventReserveQty</c> instance for the specified item and dimensions
///     or null if item is not WHS enabled.
/// </returns>
private WHSInventReserveQty whsInventReserveQty(InventSum _inventSum)
{
    WHSInventReserveQty ret = null;
    str                 key;
    InventTable         inventTable;
    InventDim           inventDimCriteria;
    InventDimParm       inventDimParm;

    if (isConfigurationkeyEnabled(configurationKeyNum(WHSandTMS))
    && WHSInventEnabled::exist(_inventSum.ItemId))
    {
        if (!_inventSum.hasRelatedTable(tableStr(InventDim)))
        {
            // If related table is missing it means that the join with InventDim has been removed.
            // Update the logic accordingly.
            throw error(Error::wrongUseOfFunction(funcName()));
        }

        inventDimCriteria.data(_inventSum.relatedTable(tableStr(InventDim)));

        key = strFmt('%1_%2_%3', inventDimCriteria.mapKey(), _inventSum.ItemId, _inventSum.DataAreaId);
        if (whsInventReserveQtyMap.exists(key))
        {
            ret = whsInventReserveQtyMap.lookup(key);
        }
        else
        {
            inventTable = InventTable::find(_inventSum.ItemId);
            inventDimCriteria = InventDim::findOrCreate(inventDimCriteria);
            inventDimParm.initFromInventDim(inventDimCriteria);

            ret = WHSInventReserveQty::newFromParms(
                inventTable,
                inventDimCriteria,
                inventDimParm,
                _inventSum);
            ret.availOrdered();

            whsInventReserveQtyMap.insert(key, ret);
        }
    }

    return ret;
}

/// <summary>
/// Does initialization of the unit of measure converters.
/// </summary>
private void initUnitConverters()
{
    productInventToSecondaryConverter = EcoResProductUnitConverter::construct();
    productInventToSecondaryConverter.parmApplyRounding(NoYes::Yes);
    productInventToSecondaryConverter.parmRoundAbsoluteValue(NoYes::Yes);

    productSecondaryToInventConverter = EcoResProductUnitConverter::construct();
    productSecondaryToInventConverter.parmApplyRounding(NoYes::No);
    productSecondaryToInventConverter.parmRoundAbsoluteValue(NoYes::Yes);
}

