/// <summary>
/// Sets up the parameters of the unit of measure converters according to current selection on the form.
/// </summary>
private void setupUnitConverters()
{
    UnitOfMeasureRecId inventUnit       = UnitOfMeasure::unitOfMeasureIdBySymbol(unitIdInvent);
    UnitOfMeasureRecId secondaryUnit    = UnitOfMeasure::unitOfMeasureIdBySymbol(unitIdSecondary);

    productInventToSecondaryConverter.parmFromUnitOfMeasureRecId(inventUnit);
    productInventToSecondaryConverter.parmToUnitOfMeasureRecId(secondaryUnit);
    productInventToSecondaryConverter.parmItemId(currentItemId);
    productInventToSecondaryConverter.parmInventDimId(currentInventDim.inventDimId);

    productSecondaryToInventConverter.parmFromUnitOfMeasureRecId(secondaryUnit);
    productSecondaryToInventConverter.parmToUnitOfMeasureRecId(inventUnit);
    productSecondaryToInventConverter.parmItemId(currentItemId);
    productSecondaryToInventConverter.parmInventDimId(currentInventDim.inventDimId);
}

