display CostPriceAverage  averageCostPriceUnit(InterCompanyInventSum _inventSum)
{
    InventSum   inventSumLocal;

    if (!inventCostPriceCache)
    {
        inventCostPriceCache = InventCostPriceCache::construct();
        inventCostPriceCache.parmOnhandOnly(true);
    }

    _inventSum.toInventSum(inventSumLocal);
    inventSumLocal.InventDimId = _inventSum.InterCompanyCompanyId + currentInventDim.inventDimId;
    return productSecondaryToInventConverter.convertValue(inventCostPriceCache.costPricePcs(inventSumLocal));
}

