display InventQtyAvailOrdered  availOrdered(InterCompanyInventSum _inventSum)
{
    if (showCompany && _inventSum.InterCompanyCompanyId)
    {
        changecompany(_inventSum.InterCompanyCompanyId)
        {
            return interCompanyInventAvailabilityFormCache.availabilityFromInterCompanySumJoinedWithDim(_inventSum, inventDim_ds).availTotal();
        }
    }
    else
    {
        return _inventSum.availOrdered();
    }
}

