display InventQtyAvailPhysical availPhysical(InterCompanyInventSum _inventSum)
{
    InventSum whsInventSum;
    InventDim whsInventDim;
    InterCompanyInventDim icInventDim = _inventSum.joinChild();

    if (showCompany && _inventSum.InterCompanyCompanyId)
    {
        changecompany(_inventSum.InterCompanyCompanyId)
        {
            _inventSum.toInventSum(whsInventSum);
            icInventDim.toInventDim(whsInventDim);
            whsInventSum.relatedTable(tablestr(InventDim), whsInventDim);

            WHSInventReserveQty whsInventReserveQty = element.whsInventReserveQty(whsInventSum);
            if (!WHSInventEnabled::exist(_inventSum.ItemId) || !whsInventReserveQty)
            {
                return _inventSum.availPhysical();
            }
            else
            {
                return interCompanyInventAvailabilityFormCache.availabilityFromInterCompanySumJoinedWithDim(_inventSum, inventDim_ds).availPhysical();
            }
        }
    }
    else
    {
        return _inventSum.availPhysical();
    }
}

