public void init()
{
    Object                  formDataSource;
    Object                  caller      = element.args().caller();
    Args                    callerArgs  = caller.args();

    InventTable             inventTable;
    InventDim               inventDimLocal;
    InventDimCombination    inventDimCombination;
    InterCompanyInventDim   tmpInventDim;
    InterCompanyInventDim   tmpInventDimLocal;

    super();

    if (element.args().record())
    {
        inventTable = TradeInterCompany::inventTable(element.args().record());

        formDataSource = FormDataUtil::getFormDataSource(element.args().record());
        if (formDataSourceHasMethod(formDataSource,identifierStr('inventDim')))
        {
            inventDimLocal = formDataSource.inventDim();
        }
        else
        {
            if (element.args().dataset() == tableNum(InventDimCombination))
            {
                inventDimCombination    = element.args().record() as InventDimCombination;
                inventDimLocal          = inventDimCombination.inventDim();
            }
            else if (InventOnhandInterCompanyFilterByProdDimToggle::instance().isEnabled()
                && element.args().record().TableId == tableNum(PurchLine))
            {
                PurchLine purchLine = element.args().record() as PurchLine;
                inventDimLocal = InventDim::find(purchLine.InventDimId);
            }
        }
    }
    else
    {
        caller      = element.args().caller();
        callerArgs  = caller.args();

        inventTable = TradeInterCompany::inventTable(callerArgs.record());

        if (callerArgs.dataset() == tableNum(InventDimCombination))
        {
            inventDimCombination    = callerArgs.record() as InventDimCombination;
            inventDimLocal          = inventDimCombination.inventDim();
        }
    }

    element.design().caption(strFmt("@SYS93875",inventTable.ItemId));

    tmpInventDim.data(inventDim);
    tmpInventDimLocal.fromInventDim(inventDimLocal);

    if (InventOnhandInterCompanyFilterByProdDimToggle::instance().isEnabled()
        && element.args().record()
        && (element.args().record().TableId == tableNum(SalesLine) || element.args().record().TableId == tableNum(PurchLine)))
    {
        TradeInterCompany::createTmpInventSumWithNoWHSCalculations(inventSum, tmpInventDimLocal, inventTable.ItemId);
    }
    else
    {
        TradeInterCompany::createTmpInventSumWithNoWHSCalculations(inventSum, tmpInventDim, inventTable.ItemId);
    }

    element.inventDimSetupObject().initQueryInventDim(inventDim_DS);
}

boolean modified()
{
    boolean ret;

    ret = super();

    showCompany = this.value();

    element.inventDimSetupObject().parmShowCompany(showCompany);

    element.updateDesign(InventDimFormDesignUpdate::Active);

    inventSum_ds.executeQuery();

    return ret;
}

public int selectionChange()
{
    int ret;

    ret = super();

    element.applyUnitID();
    inventSum_ds.refresh();

    return ret;
}

boolean modified()
{
    // Remember the old value, so we can revert if necessary
    UnitOfMeasureSymbol oldUnitID = unitIdSecondary;

    boolean ret = super();

    // If we cannot convert to the new unit, revert to the old unit
    if (!EcoResProductUnitConverter::canBeConvertedGivenUnitSymbolsForReleasedProduct(inventSum.ItemId,
                                                                                      currentInventDim.inventDimId,
                                                                                      unitIdInvent,
                                                                                      this.text()))
    {
        ret = checkFailed(strFmt("@SYS28690",unitIdInvent,this.text()));
        element.unitId(true,oldUnitID);
        ctrlUnitId.update();
    }

    if (ret)
    {
        inventSum_ds.refresh();
    }

    return ret;
}

/// <summary>
/// Validates the control contents when it is typed in manually.
/// </summary>
/// <returns>
/// true if the contents is ok; otherwise, false.
/// </returns>
boolean validate()
{
    boolean ret = true;

    if (!EcoResProductUnitConverter::canBeConvertedGivenUnitSymbolsForReleasedProduct(inventSum.ItemId,
                                                                                      currentInventDim.inventDimId,
                                                                                      unitIdInvent,
                                                                                      this.text()))
    {
        ret = checkFailed(strFmt("@SYS28690",unitIdInvent,this.text()));
        this.text(unitIdSecondary);
    }

    ret = ret && super();

    return ret;
}
