void doResearch()
{
    int pos;
    using (var activityContext = element.instrumentationLogger().inventOnhandItem().doResearch())
    {
        pos = inventSum_ds.getPosition();
        inventSum_ds.research(true);
        inventSum_ds.setPosition(pos);
    }
}

public void init()
{
    if (InventOnHandWarningNotAllowedBlankDimInHierarchyFlight::instance().isEnabled())
    {
        availPhysOnExactDimsWarningImageResource = ImageReference::constructForSymbol('Warning', "@SCM:WarningNotAllowedBlankDimInHierarchy").pack();
    }
    else
    {
        availPhysOnExactDimsWarningImageResource = ImageReference::constructForSymbol('Warning', "@SCM:AvailablePhysicalOnExactDimensionsWarning").pack();
    }
    // <GEERU>
    #ISOCountryRegionCodes
    // </GEERU>

    inventSumQtyFieldsNameSet = new Set(Types::String);
    container inventSumQtyFieldsNameList = [ #InventSumQtyFieldsNameList ];
    int i;

    for (i = 1; i <= conLen(inventSumQtyFieldsNameList); i++)
    {
        inventSumQtyFieldsNameSet.add(conPeek(inventSumQtyFieldsNameList, i));
    }

    using (var activityContext = element.instrumentationLogger().inventOnhandItem().formInit())
    {
        this.initUnitConverters();

        pmfFormCtrl = PmfFormCtrl_InventOnHandItem::newFromForm(element);

        super();

        pmfFormCtrl.setDatasources(inventSum, inventDim);
        pmfFormCtrl.initPost();

        filterPane = this.control(this.controlId(FilterPaneControl::DefaultName()));

        isFormOpenedWithDynalink = this.dynalinkExists();

        if (isFormOpenedWithDynalink)
        {
            // The dynalink will restrict the query - run it as is
            InventSum_DS.autoSearch(true);
        }
        else
        {
            // ... otherwise, allow the user to filter the query.
            this.addFilterPaneRanges();
        }

        // <GEERU>
        if (!SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
        {
            costPrice_RU.frameType(FormFrameType::None);
        }
        // </GEERU>

        // Default to inventory units
        unitsComboBox.selection(InventModuleUnits::InventoryUnit);

        inventAvailabilityFormCache = InventAvailabilityFormCache::construct();

        element.updateDesign(InventDimFormDesignUpdate::Init);

        if (element.args() && element.args().record()
        && element.args().record().TableId == tableNum(InventBatch))
        {
            callerInventBatch = element.args().record();
        }

        tmpRetailkitInventItems_ds.query().dataSourceTable(tableNum(TmpRetailkitInventItems)).enabled(false);
        // Retail Kit Order
        if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
        {
            if (element.args().dataset() == tableNum(RetailKitOrder))
            {
                tmpRetailkitInventItems_ds.query().dataSourceTable(tableNum(TmpRetailkitInventItems)).enabled(true);
                inventsum_ds.query().dataSourceTable(tableNum(TmpRetailkitInventItems)).addLink(fieldNum(InventSum, ItemId), fieldNum(TmpRetailkitInventItems, ItemId));
                element.setupRetailKitItems(element.args().record());
            }
        }

        inventOnhandDimensionDisplayHelper = new InventOnhandDimensionDisplayHelper();
        element.refreshDisplay();
    }
}

