    productInventToSecondaryUnitConverter.parmApplyRounding(NoYes::Yes);
    productSecondaryToInventUnitConverter.parmApplyRounding(NoYes::No);
    productInventToSecondaryUnitConverter.parmRoundAbsoluteValue(NoYes::Yes);
    productSecondaryToInventUnitConverter.parmRoundAbsoluteValue(NoYes::Yes);
}

