/// <summary>
/// Sets up the retail kit components and substitutes based on retail kit order.
/// </summary>
/// <param name="_retailKitOrder">
/// Reference to Retail Kit Order to retrieve the data
/// </param>
private void setupRetailKitItems(RetailKitOrder _retailKitOrder)
{
    EcoResDistinctProductVariant ecoResDistinctProductVariant;
    EcoResDistinctProductVariant substituteEcoResDistinctProductVariant;
    RetailKitComponent  retailKitComponent;
    RetailKitComponentSubstitute retailKitComponentSubstitute;
    Set kitElementsItemIds = new Set(Types::String);
    SetEnumerator enumerator = kitElementsItemIds.getEnumerator();

    using (var activityContext = element.instrumentationLogger().inventOnhandItem().setupRetailKitItems())
    {
        changecompany(_retailKitOrder.InventDataAreaId)
        {
            delete_from retailkitInventItems_local;

            kitElementsItemIds.add(InventTable::findByProductInCompany(RetailKit::find(_retailKitOrder.Kit).ProductMaster,_retailKitOrder.InventDataAreaId).ItemId);

            while select Component from retailKitComponent
                where retailKitComponent.Kit == _retailKitOrder.Kit
                outer join ProductMaster from ecoResDistinctProductVariant
                where ecoResDistinctProductVariant.RecId == retailKitComponent.Component
            {
                if (!ecoResDistinctProductVariant)
                {
                    kitElementsItemIds.add(InventTable::findByProductInCompany(retailKitComponent.Component,_retailKitOrder.InventDataAreaId).ItemId);
                }
                else
                {
                    kitElementsItemIds.add(InventTable::findByProductInCompany(ecoResDistinctProductVariant.ProductMaster,_retailKitOrder.InventDataAreaId).ItemId);
                }

                while select SubstituteProduct from retailKitComponentSubstitute
                    where retailKitComponentSubstitute.KitComponent == retailKitComponent.RecId
                    outer join ProductMaster from substituteEcoResDistinctProductVariant
                    where substituteEcoResDistinctProductVariant.RecId == retailKitComponentSubstitute.SubstituteProduct
                {
                    if (!ecoResDistinctProductVariant)
                    {
                        kitElementsItemIds.add(InventTable::findByProductInCompany(retailKitComponentSubstitute.SubstituteProduct, _retailKitOrder.InventDataAreaId).ItemId);
                    }
                    else
                    {
                        kitElementsItemIds.add(InventTable::findByProductInCompany(substituteEcoResDistinctProductVariant.ProductMaster, _retailKitOrder.InventDataAreaId).ItemId);
                    }
                }
            }

            enumerator = kitElementsItemIds.getEnumerator();
            while (enumerator.moveNext())
            {
                tmpRetailkitInventItems.ItemId = enumerator.current();
                tmpRetailkitInventItems.Kit = _retailKitOrder.Kit;
                tmpRetailkitInventItems.insert();
            }
        }
    }
}

/// <summary>
/// Sets up the parameters of the unit of measure converters according to current selection on the form.
/// </summary>
private void setupUnitConverters()
{
    productInventToSecondaryUnitConverter.parmFromUnitOfMeasureSymbol(unitIdInvent);
    productInventToSecondaryUnitConverter.parmToUnitOfMeasureSymbol(unitIdSecondary);

    productInventToSecondaryUnitConverter.parmItemId(currentItemId);
    productInventToSecondaryUnitConverter.parmInventDimId(currentInventDim.inventDimId);

    productSecondaryToInventUnitConverter.parmFromUnitOfMeasureSymbol(unitIdSecondary);
    productSecondaryToInventUnitConverter.parmToUnitOfMeasureSymbol(unitIdInvent);
    productSecondaryToInventUnitConverter.parmItemId(productInventToSecondaryUnitConverter.parmItemId());
    productSecondaryToInventUnitConverter.parmInventDimId(productInventToSecondaryUnitConverter.parmInventDimId());
}

edit UnitOfMeasureSymbol unitId(boolean _set, UnitOfMeasureSymbol _unitId)
{
    unitSymbolComboBoxCurrent.observe();
    if (_set)
    {
        unitIdSecondary = _unitId;
        this.setupUnitConverters();
        unitSymbolCurrent.markChanged();
    }

    return unitIdSecondary;
}

