    return unitIdInvent;
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init          :
            reserveOnOrdered = InventParameters::find().ReserveOnOrdered;
            RowAvailReserveGroupPhysical.visible(!reserveOnOrdered);
            RowAvailableForReservation.visible(reserveOnOrdered);
        // No break
        case InventDimFormDesignUpdate::LinkActive    :
            element.inventDimSetupObject().initFromCaller();
            element.inventDimSetupObject().parmCtrlItemId(inventSum_ItemId);
            if (#PdsCatchWeightEnabled)
            {
                element.inventDimSetupObject().parmCtrlCWItemId(InventSum_ItemId);
            }
            if (_mode == InventDimFormDesignUpdate::Init)
            {
                if (element.inventDimSetupObject().parmDimParmVisibleGrid().ItemIdFlag == NoYes::No)
                {
                    itemName.visible(false);
                }
            }
            element.inventDimSetupObject().parmDimParmVisible(element.inventDimSetupObject().parmDimParmVisibleGrid());
        // No break
        case InventDimFormDesignUpdate::Active        :
            element.inventDimSetupObject().formActiveSetup();
            element.inventDimSetupObject().formSetControls(true,true);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

public void updateUnits(InventSum _inventSum)
{
    unitSymbolCurrent.observe();
    if (!currentItemId
        || _inventSum.ItemId != currentItemId
        || !currentInventDim.inventDimId
        || !element.compareProductDimensions(currentInventDim, element.currentInventDim()))
    {
        currentItemId = _inventSum.ItemId;
        currentInventDim = element.currentInventDim();

        unitIdInvent = _inventSum.inventTable().inventUnitId();

        element.applyUnitID();

        if (!EcoResProductUnitConverter::canBeConvertedGivenUnitSymbolsForReleasedProduct(
                inventSum.ItemId,
                currentInventDim.inventDimId,
                unitIdInvent,
                unitIdSecondary))
        {
            unitIdSecondary = unitIdInvent;
            unitsComboBox.selection(InventModuleUnits::InventoryUnit);
        }

        ctrlUnitId.update();
        this.setupUnitConverters();
    }
}

public void run()
{
    using (var activityContext = element.instrumentationLogger().inventOnhandItem().formRun())
    {
        FormRunConfigurationScope configurationScope;
        boolean enableSavedViewFlight = InventOnhandItemEnableSavedViewFlight::instance().isEnabled();

        if (!isFormOpenedWithDynalink && enableSavedViewFlight)
        {
            configurationScope = this.configurationHelper().getLoadedConfigurationProfile().Scope;

            if (configurationScope != FormRunConfigurationScope::PlatformLocked)
            {
                InventSum_ds.autoSearch(true);
            }
        }

        super();

        if (isFormOpenedWithDynalink)
        {
            // The form would automatically open with details. We want to allow further slicing and dicing in the grid.
            element.task(#taskSwitchToGridView);
        }
        else
        {
            if (enableSavedViewFlight)
            {
                configurationScope = this.configurationHelper().getLoadedConfigurationProfile().Scope;
            }

            if (!enableSavedViewFlight || configurationScope == FormRunConfigurationScope::PlatformLocked)
            {
                xSysLastValue::getLast(this);
            }

            this.openFilterPane();
        }

        // Column display selection might have been loaded from SysLastValue. Raise an event to show/hide the columns accordingly.
        CWInventQuantityDisplay.modified();
    }
}

/// <summary>
/// Adds ranges to filter pane.
/// </summary>
protected void addFilterPaneRanges()
{
    QueryBuildDataSource inventSumQbds = InventSum_DS.query().dataSourceName(InventSum_DS.name());
    SysQuery::findOrCreateRange(inventSumQbds, fieldNum(InventSum, ItemId));

    QueryBuildDataSource inventTableQbds = InventTable_DS_DS.query().dataSourceName(InventTable_DS_DS.name());
    SysQuery::findOrCreateRange(inventTableQbds, fieldNum(InventTable, NameAlias));

    QueryBuildDataSource inventDimQbds = InventDim_DS.query().dataSourceName(InventDim_DS.name());
    SysQuery::findOrCreateRange(inventDimQbds, fieldNum(InventDim, InventSiteId));
    SysQuery::findOrCreateRange(inventDimQbds, fieldNum(InventDim, InventLocationId));
    SysQuery::findOrCreateRange(inventDimQbds, fieldNum(InventDim, inventSerialId));
    SysQuery::findOrCreateRange(inventDimQbds, fieldNum(InventDim, inventBatchId));
}

private void openFilterPane()
{
    if (!isFormOpenedWithDynalink)
    {
        filterPane.OpenFilterPane();
        filterPane.setFocus();

        filterMessageId = Message::Add(MessageSeverity::Informational, "@CLIFiltering:FilterOnOpen_PleaseFilter");
    }
}

private boolean dynalinkExists()
{
    return (InventSum_DS.query().dataSourceName(InventSum_DS.name()).dynalinkCount() > 0)
        || (InventTable_DS_DS.query().dataSourceName(InventTable_DS_DS.name()).dynalinkCount() > 0)
        || (InventDim_DS.query().dataSourceName(InventDim_DS.name()).dynalinkCount() > 0)
        || (InventBatch_DS.query().dataSourceName(InventBatch_DS.name()).dynalinkCount() > 0);
}

public int task(int _taskId)
{
    if (_taskId == #taskRefresh && !isQueryExecuted)
    {
        // Workaround for platform paging issue
        filterPane.ApplyFilter();

        return 0;
    }

    int ret = super(_taskId);

    if (_taskId == #taskSwitchToGridView
        && wasFilterPaneVisible
        && !filterPane.isVisible())
    {
        filterPane.OpenFilterPane();
    }

    return ret;
}

public container pack()
{
    return [#CurrentVersion, InventSum_ds.queryRun().pack(), CWInventQuantityDisplay.selection()];
}

public boolean unpack(container packedClass)
{
    Version version = RunBase::getVersion(packedClass);

    switch (version)
    {
        case #CurrentVersion:
            container queryCon;
            PdsCWInventQuantityDisplay cwInventQuantityDisplayPacked;
            [version, queryCon, cwInventQuantityDisplayPacked] = packedClass;

            if (SysQuery::isPackedOk(queryCon))
            {
                InventSum_ds.queryRun(new QueryRun(queryCon));
                CWInventQuantityDisplay.selection(cwInventQuantityDisplayPacked);

                return true;
            }
            break;
    }

    return false;
}

