public void close()
{
    // We only want to remember filter values when the filter is shown, i.e. when coming to the form without a record.
    // Queries with dynalinks can't be packed, so check if no were added dynamically.
    if (!isFormOpenedWithDynalink && !this.dynalinkExists())
    {
        xSysLastValue::saveLast(this);
    }

    super();
}

