display CostPriceAverage  averageCostPriceUnit(InventSum _inventSum)
{
    InventDim dim;

    // Fields might not have been selected on the specified buffers, or might have been updated since selection
    _inventSum.checkInvalidFieldAccess(false);

    element.updateUnits(_inventSum);

    if (!_inventSum.InventDimId)
    {
        dim = InventDim::findDim(this.inventDim());
        if (dim.InventDimId)
        {
            _inventSum.InventDimId = dim.InventDimId;
        }
    }

    if (!inventCostPriceCache)
    {
        inventCostPriceCache = InventCostPriceCache::construct();
        inventCostPriceCache.parmOnhandOnly(true);
    }

    return CurrencyExchangeHelper::price(inventCostPriceCache.costPricePcs(_inventSum, this.inventDim()) * productSecondaryToInventUnitConverter.convertValue(1));
}

void linkActive()
{
    element.updateDesign(InventDimFormDesignUpdate::LinkActive);
    super();

    if (element.args()
        && element.args().dataset())
    {
        FieldId callerItemIdFieldId = fieldName2id(element.args().dataset(), fieldStr(InventSum, ItemId));

        if (callerItemIdFieldId)
        {
            InventTable inventTable = InventTable::find(element.args().record().(callerItemIdFieldId));

            if (inventTable.RecId
                && inventTable.inventItemType().mustInventBeControlled() == false
                && inventTable.isStocked())
            {
                info(strFmt("@SCM:ViewInventOnhandShowMsgWhenItemIsService", inventTable.ItemId));
            }
        }
    }
}

void init()
{
    super();

    element.inventDimSetupObject().initQueryInventSum(inventSum_DS);            
}

void executeQuery()
{
    using (var activityContext = element.instrumentationLogger().inventOnhandItem().inventSumExecuteQuery())
    {
        isQueryExecuted = true;

        inventCostPriceCache = InventCostPriceCache::construct();
        inventCostPriceCache.parmOnhandOnly(true);

        element.inventDimSetupObject().modifyQuery(inventSum_DS, inventDim_DS);
        if (inventSum_DS.queryRun() && inventSum_DS.queryRun().query())
        {
            element.inventDimSetupObject().modifyQueryBasedOnDatasourceName(
                inventSum_DS.queryRun().query(),
                inventSum_DS.name(),
                inventDim_DS);
        }

        inventAvailabilityFormCache.refreshCache();

        super();

        if (inventSum_DS.queryRun() && inventSum_DS.queryRun().query())
        {
            Query inventSumQuery = InventSum_ds.queryRun().query();
            QueryFilter queryFilter;
            int i;

            for (i = 1; i <= inventSumQuery.queryFilterCount(); i++)
            {
                queryFilter = inventSumQuery.queryFilter(i);
                var fieldName = queryFilter.field();

                if (inventSumQtyFieldsNameSet.in(fieldName))
                {
                    warning(strFmt("@SCM:InventOnHandItemFilterOnAggregateQtyWarning", fieldName));
                    break;
                }
            }
        }

        if (filterMessageId)
        {
            Message::Remove(filterMessageId);
            filterMessageId = 0;
        }
    }
}

int active()
{
    int ret;

    using (var activityContext = element.instrumentationLogger().inventOnhandItem().inventSumActive())
    {
        inventSum.checkInvalidFieldAccess(false);

        element.updateUnits(inventSum);

        if (#PdsEnabled)
        {
            inventDim.checkInvalidFieldAccess(false);
            element.resetDispShelfQty();
            this.pdsNonNetExpiredBatch(inventSum);
        }

        ret = super();

        inventDim_ds.active();

        if (#pdsShelfEnabled)
        {
            inventBatch_ds.executeQuery();
        }

        pmfFormCtrl.dataSourceActivePost();
    }

    return ret;
}

