/// <summary>
/// Check if the selected dimension set creates a hole in the reservation hierarchy.
/// </summary>
/// <param name="_inventSum">
/// The <c>InventSum</c> datasource's table buffer.
/// </param>
/// <returns>
/// A warning icon if the dimension setup is invalid, otherwise a blank icon.
/// </returns>
public display container displayInvalidDimensionWarning(InventSum _inventSum)
{
    container ret;

    if (WHSInventEnabled::exist(_inventSum.ItemId))
    {
        InventDim joinedInventDim;
        joinedInventDim.data(_inventSum.joinChild());

        if (element.anyNotAllowedBlankDimInHierarchy(_inventSum.inventTable(), joinedInventDim))
        {
            ret = availPhysOnExactDimsWarningImageResource;
        }
    }

    return ret;
}

/// <summary>
/// Find the available physical inventory for the displayed dimension set.
/// </summary>
/// <param name="_inventSum">
/// The <c>InventSum</c> datasource's table buffer.
/// </param>
/// <returns>
/// The available physical quantity for the exact dimension set displayed.
/// </returns>
public display InventQtyAvailPhysical displayAvailPhysExactDims(InventSum _inventSum)
{
    Qty availPhysicalLocal;

    if (!WHSInventEnabled::exist(_inventSum.ItemId))
    {
        return this.availPhysical(_inventSum);
    }

    // If we have a hole then we will not find a value for the exact dimensions so we return 0
    if (element.anyNotAllowedBlankDimInHierarchy(_inventSum.inventTable(), _inventSum.joinChild()))
    {
        availPhysicalLocal = 0;
    }
    else
    {
        //if we do not have a hole we have the value for the deepest level calculated and can use that directly to avoid further RPC/DB calls
        availPhysicalLocal = inventAvailabilityFormCache.whsSpecificAvailabilityFromSumJoinedWithDim(_inventSum, InventDim_ds).availPhysicalLevel();
    }

    return availPhysicalLocal;
}

 public display PdsCWQty displayCWAvailPhysExactDims(InventSum _inventSum)
 {
     if (!WHSInventTable::isItemWHSAndCatchWeightEnabled(_inventSum.Itemid)
         ||  element.anyNotAllowedBlankDimInHierarchy(_inventSum.inventTable(), _inventSum.joinChild()))
     {
         return 0;
     }            

     return inventAvailabilityFormCache.whsSpecificAvailabilityFromSumJoinedWithDim(_inventSum, InventDim_ds).cwAvailPhysicalLevel();
}

