display CostPriceAverage averageCostPriceSecCur_RU(InventSum _inventSum)
{
    InventDim dim;

    element.updateUnits(_inventSum);

    if (!_inventSum.InventDimId)
    {
        dim = InventDim::findDim(this.inventDim());
        if (dim.InventDimId)
        {
            _inventSum.InventDimId = dim.InventDimId;
        }
    }

    return CurrencyExchangeHelper::price(_inventSum.costPriceSecCurPcs_RU() * productSecondaryToInventUnitConverter.convertValue(1), CompanyInfoHelper::secondaryCurrency_RU());
}

