/// <summary>
///     Calculates the available ordered quantity of the item, by the display unit.
/// </summary>
/// <param name="_inventSum">
///     InventSum record used to calculate the available ordered quantity.
/// </param>
/// <returns>
///     The available ordered for the active inventSum record.
/// </returns>
public display PdsInventQtyAvailOrdered pdsBatchAvailOrderedUnit(InventSum _inventSum)
{
    InventQtyAvailOrdered   availOrderedLocal;
    element.updateUnits(_inventSum);

    availOrderedLocal = inventAvailabilityFormCache.availabilityFromSumJoinedWithDim(_inventSum, inventDim_ds).availTotal();

    if (#PdsShelfEnabled)
    {
        availOrderedLocal -= nonNetQty;
        availOrderedLocal -= expQty;
    }

    return productInventToSecondaryUnitConverter.convertValue(availOrderedLocal);
}

/// <summary>
/// Calculated and returns the available ordered CW qty.
/// </summary>
/// <param name="_inventSum">
/// InventSum record used to calculate Avail ordered quantity.
/// </param>
/// <returns>
/// The available ordered CW qty for the active inventSum record.
/// </returns>
public display PdsCWAvailOrdered pdsBatchCWAvailOrdered(InventSum _inventSum)
{
    PdsCWAvailOrdered   availBatchOrdered;

    availBatchOrdered   = _inventSum.pdsCWAvailOrderedCalculated();

    if (#PdsShelfEnabled)
    {
        availBatchOrdered -= cwNonNetQty;
        availBatchOrdered -= cwExpQty;
    }

    element.updateUnits(_inventSum);

    return availBatchOrdered;
}

/// <summary>
/// Calculates the non netable physical invent and cw quantities,
/// and well as the expired physical invent and CW quantities.
/// </summary>
/// <param name="_inventSum">
/// InventSum record used to calculate quantities.</param>
/// <remarks>
/// <syntax>
/// public void pdsNonNetExpireBatch(InventSum _inventSum = InventSum)
/// </syntax>
/// </remarks>
public void pdsNonNetExpiredBatch(InventSum _inventSum = inventSum)
{
    if (_inventSum)
    {
        using (var activityContext = element.instrumentationLogger().inventOnhandItem().inventSumPdsNonNetExpiredBatch())
        {
            if (#PdsEnabled)
            {
                [nonNetQty, cwNonNetQty] = _inventSum.pdsNonNetPhysical(
                                            inventDim,
                                            callerInventBatch);
            }

            if (#PdsShelfEnabled
            && _inventSum.inventTable().isShelfLifeItem())
            {
                [expQty,cwExpQty]        = _inventSum.pdsExpiredPhysical(
                                            inventDim,
                                            callerInventBatch);
            }
        }
    }
    else
    {
        nonNetQty = 0;
        cwNonNetQty = 0;
        expQty = 0;
        cwExpQty = 0;
    }
}

