public void executeQuery()
{
    using (var activityContext = element.instrumentationLogger().inventOnhandItem().inventBatchExecuteQuery())
    {
        if (#pdsShelfEnabled)
        {
            SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(InventBatch)),fieldNum(InventBatch,ItemId)).value(queryValue(inventSum.ItemId));
            SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(InventBatch)),fieldNum(InventBatch,InventBatchId)).value(queryValue(inventDim.InventBatchId));
        }
        super();
    }
}

void clicked()
{
    MenuFunction            menuFunction;
    FormRun                 configForm;
    Args                    args;
    InventTable             inventTable;
    InventSum               inventSumLocal = inventSum;

    if (!inventSumLocal.ItemId)
    {
        inventTable           = TradeInterCompany::inventTable(element.args().record());
        inventSumLocal.ItemId = inventTable.ItemId;
    }

    menuFunction = new MenuFunction(menuitemDisplayStr(InventOnHandInterCompany), MenuItemType::Display);
    args = new Args();
    args.caller(element);
    args.record(inventSumLocal);
    configForm = menuFunction.create(args);
    configForm.run();
}

public void clicked()
{
    if (!isQueryExecuted)
    {
        // Workaround for platform paging issue. We would get "Aggregate Queries not supported for Value Paging" after coming back to the form
        // if the query was not run before.
        filterPane.ApplyFilter();
    }

    super();
}

public void pageActivated()
{
    super();

    inventSum_ds.pdsNonNetExpiredBatch();
}

public int selectionChange()
{
    int ret;

    ret = super();

    element.applyUnitID();
    unitSymbolComboBoxCurrent.markChanged();
    inventSum_ds.refresh();

    return ret;
}

boolean modified()
{
    boolean ret;
    UnitOfMeasureSymbol oldUnitID;

    // Remember the old value, so we can revert if necessary
    oldUnitID = unitIdSecondary;

    ret = super();

    // If we cannot convert to the new unit, revert to the old unit
    if (!EcoResProductUnitConverter::canBeConvertedGivenUnitSymbolsForReleasedProduct(inventSum.ItemId,
                                                                                      currentInventDim.inventDimId,
                                                                                      unitIdInvent,
                                                                                      this.text()))
    {
        ret = checkFailed(strFmt("@SYS28690",unitIdInvent,this.text()));
        element.unitId(true,oldUnitID);
        ctrlUnitId.update();
    }

    if (ret)
    {
        inventSum_ds.refresh();
    }
    return ret;
}

/// <summary>
/// Validates the control contents when it is typed in manually.
/// </summary>
/// <returns>
/// true if the contents is ok; otherwise, false.
/// </returns>
boolean validate()
{
    boolean ret = true;

    if (!EcoResProductUnitConverter::canBeConvertedGivenUnitSymbolsForReleasedProduct(inventSum.ItemId,
                                                                                      currentInventDim.inventDimId,
                                                                                      unitIdInvent,
                                                                                      this.text()))
    {
        ret = checkFailed(strFmt("@SYS28690",unitIdInvent,this.text()));
        this.text(unitIdSecondary);
    }

    ret = ret && super();

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    inventDimFormSetup.parmInventProfileType_RU(inventProfileType_RU.selection());
    inventSum_ds.executeQuery();

    return ret;
}

