[Form]
public class InventPosting extends FormRun
{
    InventPostingForm inventPostingForm;

    Integer           lastSel;

    const int         OtherPostingType     = 0;
    const int         InventoryPostingType = 1;
    const int         WIPPostingType       = 2;

    int               lastSalesPostingType,
                      lastInventoryPostingType,
                      lastProductionPostingType,
                      lastPurchPostingType;

    // <GIN>
    boolean                                 countryRegion_IN;
    // </GIN>
    // <GEERU>
    boolean                                 countryRegion_RU;
    // </GEERU>

    // <GIN><GEERU>
    #ISOCountryRegionCodes
    // </GEERU></GIN>

}

public void init()
{
    // <GCN>
    boolean ledgerAcctSitesEnabled_CN;
    // </GCN>
    // <GEERU>
    countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    // </GEERU>
    // <GIN>
    countryRegion_IN = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
    // </GIN>

    super();

    inventPostingForm = new InventPostingForm(element.args().record());

    // <GCN>
    ledgerAcctSitesEnabled_CN = InventPostingExtendParm_CN::checkExtendParmEnabled();
    if (ledgerAcctSitesEnabled_CN)
    {
        GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceCN00006, funcName());
    }
    sales_ds.object(fieldNum(InventPosting, SiteCode_CN)).visible(ledgerAcctSitesEnabled_CN);
    sales_ds.object(fieldNum(InventPosting, SiteRelation_CN)).visible(ledgerAcctSitesEnabled_CN);
    purch_ds.object(fieldNum(InventPosting, SiteCode_CN)).visible(ledgerAcctSitesEnabled_CN);
    purch_ds.object(fieldNum(InventPosting, SiteRelation_CN)).visible(ledgerAcctSitesEnabled_CN);
    invent_ds.object(fieldNum(InventPosting, SiteCode_CN)).visible(ledgerAcctSitesEnabled_CN);
    invent_ds.object(fieldNum(InventPosting, SiteRelation_CN)).visible(ledgerAcctSitesEnabled_CN);
    prod_ds.object(fieldNum(InventPosting, SiteCode_CN)).visible(ledgerAcctSitesEnabled_CN);
    prod_ds.object(fieldNum(InventPosting, SiteRelation_CN)).visible(ledgerAcctSitesEnabled_CN);
    variance_ds.object(fieldNum(InventPosting, SiteCode_CN)).visible(ledgerAcctSitesEnabled_CN);
    variance_ds.object(fieldNum(InventPosting, SiteRelation_CN)).visible(ledgerAcctSitesEnabled_CN);
    // </GCN>

    // Initialize all datasource ranging based on default radio button selections
    inventPostingForm.querySales(sales_ds, ctrlSalesOrder.selectionAsInventAccountTypeSales());
    inventPostingForm.queryPurch(purch_ds, ctrlPurch.selectionAsInventAccountTypePurch());
    inventPostingForm.searchItemIdx(invent_ds, InventPosting::inventAccount2AccountType(ctrlInvent.selectionAsInventAccountTypeInvent()));
    inventPostingForm.queryProd(prod_ds, ctrlProd.selectionAsInventAccountTypeProd());
    inventPostingForm.queryVariance(variance_ds, ctrlVariance.selectionAsInventAccountTypeStdCostVariance());

    // <GIN>
    if (countryRegion_IN)
    {
        ctrlTabStkTransfer_IN.visible(TaxParameters::checkTaxParameters_IN());
        inventPostingForm.queryTransferOrder_IN(transferOrder_IN_ds, ctrlStkTransfer_IN.selection());
    }
    // </GIN>

    this.updateInvent_AccountNumSegmentedEntryControl();
    this.updateProd_AccountNumSegmentedEntryControl();
    this.updatePurch_AccountNumSegmentedEntryControl();
    this.updateSales_AccountNumSegmentedEntryControl();
}

private int classifyPostingType(LedgerPostingType _postingType)
{
    int ret = OtherPostingType;
    if (InventPosting::isInventoryPostingType(_postingType))
    {
        ret = InventoryPostingType;
    }
    else if (InventPosting::isWIPPostingType(_postingType))
    {
        ret = WIPPostingType;
    }

    return ret;
}

private void updateSales_AccountNumSegmentedEntryControl()
{
    LedgerPostingType ledgerPostingType = InventPosting::accountType2LedgerPostingType(InventPosting::salesAccount2AccountType(ctrlSalesOrder.selectionAsInventAccountTypeSales()));
    int newPostingTypeClassification = this.classifyPostingType(ledgerPostingType);

    if (newPostingTypeClassification != lastSalesPostingType)
    {
        SalesOrder_AccountNum.parmPostingType(ledgerPostingType);
        lastSalesPostingType = newPostingTypeClassification;
    }
}

private void updatePurch_AccountNumSegmentedEntryControl()
{
    LedgerPostingType ledgerPostingType = InventPosting::accountType2LedgerPostingType(InventPosting::purchAccount2AccountType(ctrlPurch.selectionAsInventAccountTypePurch()));
    int newPostingTypeClassification = this.classifyPostingType(ledgerPostingType);

    if (newPostingTypeClassification != lastPurchPostingType)
    {
        Purch_AccountNum.parmPostingType(ledgerPostingType);
        lastPurchPostingType = newPostingTypeClassification;
    }
}

private void updateInvent_AccountNumSegmentedEntryControl()
{
    LedgerPostingType ledgerPostingType = InventPosting::accountType2LedgerPostingType(InventPosting::inventAccount2AccountType(ctrlInvent.selectionAsInventAccountTypeInvent()));
    int newPostingTypeClassification = this.classifyPostingType(ledgerPostingType);

    if (newPostingTypeClassification != lastInventoryPostingType)
    {
        Invent_AccountNum.parmPostingType(ledgerPostingType);
        lastInventoryPostingType = newPostingTypeClassification;
    }
}

private void updateProd_AccountNumSegmentedEntryControl()
{
    LedgerPostingType ledgerPostingType = InventPosting::accountType2LedgerPostingType(InventPosting::prodAccount2AccountType(ctrlProd.selectionAsInventAccountTypeProd()));
    int newPostingTypeClassification = this.classifyPostingType(ledgerPostingType);

    if (newPostingTypeClassification != lastProductionPostingType)
    {
        Prod_AccountNum.parmPostingType(ledgerPostingType);
        lastProductionPostingType = newPostingTypeClassification;
    }
}

