{
    element.setTabPage();
    inventPostingForm.updateNow();

    super();
}

/// <summary>
/// Enable or disable relations fields depending on the InventProfileTypeAll_RU field.
/// </summary>
/// <param name="_inventPosting">
/// The current InventPosting table record to enable or disable form fields for.
/// </param>
void setInventProfileRelationEnabled_RU(InventPosting _inventPosting)
{
    boolean allowEdit;
    FormDataObject formDataObject;

    if (FormDataUtil::isFormDataSource(_inventPosting))
    {
        allowEdit = _inventPosting.isInventProfileTypeFieldRequired_RU();
        formDataObject = FormDataUtil::getFormDataSource(_inventPosting).object(fieldNum(InventPosting, InventProfileType_RU));
        formDataObject.allowEdit(allowEdit);
        formDataObject.skip(!allowEdit);

        allowEdit = _inventPosting.isInventProfileIdFieldRequired_RU();
        formDataObject = FormDataUtil::getFormDataSource(_inventPosting).object(fieldNum(InventPosting, InventProfileId_RU));
        formDataObject.allowEdit(allowEdit);
        formDataObject.skip(!allowEdit);
    }
}

