void setTabPage()
{
    if (element.args().parmEnumType())
    {
        switch (element.args().parmEnum())
        {
            case InventAccountType::SalesCommission :
                element.toggleCommission(false);
                inventPostingForm.parmCommissionFlag(true);

                ctrlSalesOrder.selection(enum2int(InventAccountTypeSales::Commission));
                element.design().caption("@SYS25846");
                break;

            default :
                element.toggleCommission(true);
        }
    }
    else
    {
        element.toggleCommission(true);

        switch (element.args().dataset())
        {
            case tableNum(CustTable),tableNum(VendTable),
                 tableNum(CustGroup),tableNum(VendGroup):
                ctrlTabInvent.visible(false);
                ctrlTabVariance.visible(false);
                ctrlTabProd.visible(false);
                break;
        }
        switch (element.args().dataset())
        {
            case tableNum(CustTable),tableNum(CustGroup):
                ctrlTabPurch.visible(false);
                ctrlTab.showTabs(false);
                break;
            case tableNum(VendTable),tableNum(VendGroup):
                ctrlTabSales.visible(false);
                ctrlTab.showTabs(false);
                ctrlTabPurch.pageActivated();
                break;
            case tableNum(BOMCostGroup):
                ctrlTabInvent.visible(false);
                ctrlTabProd.visible(false);
                ctrlTabPurch.visible(false);
                ctrlTabSales.visible(false);
                ctrlTabVariance.pageActivated();
                break;
        }
    }
}

void toggleCommission(boolean set = true)
{
    ctrlSalesOrder.visible(set);
    ctrlCommission.visible(! set);

    ctrlPurch.visible(set);
    ctrlInvent.visible(set);
    ctrlProd.visible(set);
    ctrlTabPurch.visible(set);
    ctrlTabInvent.visible(set);
    ctrlTabProd.visible(set);
    ctrlTabVariance.visible(set);

    ctrlTab.showTabs(set);
}

