void initValue()
{
    super();

    inventPostingForm.initFields(sales, InventPosting::salesAccount2AccountType(ctrlSalesOrder.selectionAsInventAccountTypeSales()));
}

int active()
{
    int         ret;

    element.setRelationsEnabled(sales);
    // <GEERU>
    if (countryRegion_RU)
    {
        element.setInventProfileRelationEnabled_RU(sales);
    }
    // </GEERU>

    sales_ds.object(fieldNum(InventPosting, CustVendCode)).allowEdit(true);
    sales_ds.object(fieldNum(InventPosting, CustVendCode)).skip(false);
    sales_ds.object(fieldNum(InventPosting, CustVendRelation)).allowEdit(sales.CustVendCode != TableGroupAll::All);
    sales_ds.object(fieldNum(InventPosting, CustVendRelation)).skip(!(sales.CustVendCode != TableGroupAll::All));
    sales_ds.object(fieldNum(InventPosting, TaxGroupId)).allowEdit(true);
    sales_ds.object(fieldNum(InventPosting, TaxGroupId)).skip(false);

    // <GCN>
    if (InventPostingExtendParm_CN::checkExtendParmEnabled())
    {
        element.setSiteCodeEnabled_CN(sales);
        element.setSiteRelationsEnabled_CN(sales);
    }
    // </GCN>

    ret = super();

    return ret;
}

