public void cursorNotify(int _event)
{
    super(_event);
    variance_ds.object(fieldNum(InventPosting, CostCode)).allowEdit(variance.InventAccountType != InventAccountType::InventStdCostRoundingVariance);
    variance_ds.object(fieldNum(InventPosting, CostCode)).skip(!(variance.InventAccountType != InventAccountType::InventStdCostRoundingVariance));
    variance_ds.object(fieldNum(InventPosting, CostRelation)).allowEdit(variance.InventAccountType != InventAccountType::InventStdCostRoundingVariance);
    variance_ds.object(fieldNum(InventPosting, CostRelation)).skip(!(variance.InventAccountType != InventAccountType::InventStdCostRoundingVariance));
}

