{
    super();

    transferOrder_IN_ds.object(fieldNum(InventPosting, ItemRelation)).allowEdit(transferOrder_IN.ItemCode != InventPostingItemCode::All);
    transferOrder_IN_ds.object(fieldNum(InventPosting, ItemRelation)).skip(!(transferOrder_IN.ItemCode != InventPostingItemCode::All));
    element.setRelationsEnabled(transferOrder_IN);
}

public void pageActivated()
{
    super();
    element.defaultDataSource(sales_ds);
}

void enter()
{
    super();
}

public void gotFocus()
{
    super();
    lastSel = this.selection();
}

int selectionChange()
{
    int  ret;

    //<GBR>
    SysInfoAction sysInfoAction;
    //</GBR>

    //<GBR>
    sysInfoAction = SysInfoAction_Formrun::newFormnameControlname(formStr(CustParameters),identifierStr(TabLedgerVAT));

    if (this.selectionAsInventAccountTypeSales() == InventAccountTypeSales::Cancel
         && !(BrazilParameters::isEnabled() && CustParameters::isCancelAlternativePostingProfileEnabled()))
    {
        this.selection(lastSel);
        return checkFailed("@Brazil:SalesCancellingAlternativeAccount",'',sysInfoAction);
    }
    else if (this.selectionAsInventAccountTypeSales() == InventAccountTypeSales::Return && !CustParameters::isReturnAlternativePostingProfileEnabled())

    {
        this.selection(lastSel);
        return checkFailed("@Brazil:SalesReturnAlternativeAccount",'',sysInfoAction);
    }
    //</GBR>

    if (!sales_ds.leaveRecord())
    {
        this.selection(lastSel);
        return 0;
    }

    ret = super();

    // Update data source restrictions and requery
    sales_ds.executeQuery();

    element.updateSales_AccountNumSegmentedEntryControl();

    return ret;
}

public InventAccountTypeSales selectionAsInventAccountTypeSales()
{
    return any2Enum(this.selection());
}

void enter()
{
    super();

    lastSel = this.selection();
}

int selectionChange()
{
    int  ret;

    if (!sales_ds.leaveRecord())
    {
        this.selection(lastSel);
        return 0;
    }

    ret = super();

    switch (this.selection())
    {
        case CommissionAccounts::Commission :
            ctrlSalesOrder.selection(enum2int(InventAccountTypeSales::Commission));
            break;

        case CommissionAccounts::CommissioOffsetAccount :
            ctrlSalesOrder.selection(enum2int(InventAccountTypeSales::CommissionOffsetAccount));
            break;
    }
    // Update data source restrictions and execute query again
    sales_ds.executeQuery();

    return ret;
}

