public void lookup()
{
    if (variance.CostCode == TableGroupAll::GroupId)
    {
        this.lookupCostGroupType();
    }
    else
    {
        super();
    }
}

/// <summary>
/// Display a lookup with cost group types.
/// </summary>
public void lookupCostGroupType()
{
    SysDictEnum                 dictEnum;
    CostGroupType               costGroupType;
    InventCostTmpRelationLookup inventCostRelationLookup;

    Query                   query          = new Query();
    QueryBuildDataSource    queryBuildDataSource;

    // Create an instance of SysTableLookup where 'this'
    // is the current form control.
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(InventCostTmpRelationLookup), this);

    dictEnum = new SysDictEnum(enumNum(CostGroupType));
    costGroupType = dictEnum.firstValue();
    do
    {
        inventCostRelationLookup.CostRelation = strFmt('%1', dictEnum.value2Index(costGroupType));
        inventCostRelationLookup.Name = dictEnum.value2Label(costGroupType);
        inventCostRelationLookup.insert();
        costGroupType= dictEnum.nextValue(costGroupType);
    }
    while (costGroupType != dictEnum.firstValue());

    // Add fields to be shown in the lookup form.
    sysTableLookup.addLookupfield(fieldNum(InventCostTmpRelationLookup, CostRelation));
    sysTableLookup.addLookupfield(fieldNum(InventCostTmpRelationLookup, Name));

    // Limit and arrange the data selection.
    queryBuildDataSource = query.addDataSource(tableNum(InventCostTmpRelationLookup));

    sysTableLookup.parmTmpBuffer(inventCostRelationLookup);
    sysTableLookup.parmQuery(query);

    // Perform the lookup.
    sysTableLookup.performFormLookup();
}

