public void init()
{
    super();

    postalAddressForm = LogisticsEntityPostalAddressFormHandler::newParameters(inventSite_ds, logisticsEntityPostalAddressView_ds);

    tabFinancialDimensions.visible(InventParameters::find().SiteDimensionAttribute);

    if (BrazilParameters::isEnabled())
    {
        detailAddress.enabled(false);
        fiscalEstablishmentInventSite_BR_ds.object(fieldNum(FiscalEstablishmentInventSite_BR, FiscalEstablishment_BR)).mandatory(true);
    }
}

/// <summary>
///     Updates the design for Thailand feature.
/// </summary>
public void updateDesign()
{
    if (TaxThaiGovCertificationFeatureChecker::isTaxBranchEnabled()
        && !TaxBranchType::isTaxBranchDimCreatedAndBoundWithSite())
    {
        inventSite_ds.object(fieldNum(InventSite, TaxBranchRefRecId)).visible(true);
        inventSite_ds.object(fieldNum(InventSite, TaxBranchRefRecId)).allowEdit(true);
        tabTaxBranch.visible(true);
    }
    else
    {
        inventSite_ds.object(fieldNum(InventSite, TaxBranchRefRecId)).visible(false);
        inventSite_ds.object(fieldNum(InventSite, TaxBranchRefRecId)).allowEdit(false);
        tabTaxBranch.visible(false);
    }
}

public boolean validateWrite()
{
    boolean ret;
    FormRun     formRun;
    FormName    formName;

    ret = super();

    formRun = element.args().caller();
    if (formRun)
    {
        formName = formRun.name();
    }

    if (ret
        && TaxThaiGovCertificationFeatureChecker::isTaxBranchEnabled()
        && !TaxBranchType::isTaxBranchDimCreatedAndBoundWithSite()
        && formName != formStr(InventSiteDimensionLink))
    {
        if (!TaxBranch::find(inventSite.TaxBranchRefRecId))
        {
            // Select the default tax branch.
            ret = checkFailed("@SYS4082921");
        }
    }

    return ret;
}

public void write()
{
    super();

    if (BrazilParameters::isEnabled())
    {
        logisticsEntityPostalAddressView_ds.research();
    }
}

