    int ret;

    ret = super();

    element.updateDesign();

    return ret;
}

public void init()
{
    super();

    if (element.args().dataset() == tableNum(InventParameters))
    {
        this.query().dataSourceTable(tableNum(InventSite)).clearDynalinks();
    }
}

public void modified()
{
    super();

    reqSitePolicy.InventSiteId = inventSite.SiteId;

    if (BrazilParameters::isEnabled())
    {
        fiscalEstablishmentInventSite_BR.InventSite = inventSite.SiteId;
    }
}

public boolean validate()
{
    boolean                 ret;
    TaxBranchRefRecId       taxBranchRecIdOrig;
    TaxBranchCode           taxBranchCode;
    TaxBranchCode           taxBranchCodeOrig;

    ret = super();

    if (ret)
    {
        taxBranchRecIdOrig  = inventSite.orig().TaxBranchRefRecId;
        taxBranchCodeOrig   = TaxBranch::find(taxBranchRecIdOrig).Code;
        taxBranchCode       = TaxBranch::find(inventSite.TaxBranchRefRecId).Code;

        if (!TaxBranchType::confirmTaxBranchChanged(taxBranchCodeOrig, taxBranchCode))
        {
            ret = false;
        }
        else if (!inventSite.TaxBranchRefRecId)
        {
            // Select the default tax branch.
            ret = checkFailed("@SYS4082921");
        }
    }

    return ret;
}

public void init()
{
    SysDictTable sysDictTable = new SysDictTable(reqSitePolicy_ds.table());

    // The ReqSitePolicy has a different configuration key than the primary data source
    // so it must be ensured that the form works even though that key is disabled
    if (!isConfigurationkeyEnabled(sysDictTable.configurationKeyId()))
    {
        reqSitePolicy_ds.linkType(FormLinkType::Passive);
    }

    super();
}

public void write()
{
    if (inventSite.RecId == 0)
    {
        ReqSitePolicy::deleteInventSiteId(inventSite.SiteId);
    }

    super();
}

public void init()
{
    super();
    this.cacheAddMethod(tableMethodStr(LogisticsEntityPostalAddressView,locationRoles));
}

public int active()
{
    int ret;

    ret = super();

    postalAddressForm.callerActive();
    postalAddressForm.callerUpdateButtons(newAddress, editAddress, deleteAddress, mapButton, deactivateAddress, logisticsLocationDefault, logisticsEntityPostalAddressView.IsPrimary);

    return ret;
}

public void executeQuery()
{
    LogisticsEntityPostalAddressView::updateQuery(this.query().dataSourceName(this.name()), inventSite.RecId, LogisticsLocationEntityType::Site);
    super();
    postalAddressForm.callerUpdateButtons(newAddress, editAddress, deleteAddress, mapButton, deactivateAddress, logisticsLocationDefault, logisticsEntityPostalAddressView.IsPrimary);
}

