/// <summary>
/// Write on <c>InventSite_IN</c> form datasource.
/// </summary>
public void write()
{
    if (isINCountryRegion && InventSite.SiteId)
    {
        InventSite_IN.SiteId = InventSite.SiteId;
        super();
    }
}

/// <summary>
/// Initialize <c>InventSite_IN</c> form datasource.
/// </summary>
public void init()
{
    if (isINCountryRegion)
    {
        inventsite_in_ds.linkType(FormLinkType::OuterJoin);
    }

    super();
}

public void pageActivated()
{
    super();

    buttonExpandTree.text("@SYS22914");
    buttonExpandTree.helpText("@SYS104161");
    formTreeExpand   = FormTreeExpand::Collapse;
    siteIdxContainer = InventSite::fillHierarchyTree(inventSite.SiteId, formTreeControl);
}

void clicked()
{
    int             i;

    super();

    if (formTreeExpand == FormTreeExpand::Collapse)
    {
        buttonExpandTree.text("@SYS104162");
        buttonExpandTree.helpText("@SYS104163");
        formTreeExpand = FormTreeExpand::Expand;
    }
    else
    {
        buttonExpandTree.text("@SYS22914");
        buttonExpandTree.helpText("@SYS104161");
        formTreeExpand = FormTreeExpand::Collapse;
    }

    for (i = 1; i <= conLen(siteIdxContainer); i++)
    {
        formTreeControl.expand(conPeek(siteIdxContainer, i), formTreeExpand);
    }
}
