/// <summary>
/// Performs specific actions when a form is closed.
/// </summary>
public void closeOK()
{
    InventSiteGateEntryLine_IN  inventSiteGateEntryLine;
    SalesLine   salesLineLoc;
    Object InventSiteGateEntry_DataSource;

    super();

    if (inventSiteGateEntry)
    {
        InventSiteGateEntryLineCopy_IN::construct(inventSiteGateEntry, tmpFrmVirtualLines, SalesLine).transferToGateEntry();

        InventSiteGateEntry_DataSource = FormDataUtil::getFormDataSource(inventSiteGateEntry);

        InventSiteGateEntry_DataSource.reRead();
        InventSiteGateEntry_DataSource.reFresh();
        InventSiteGateEntry_DataSource.reReadReferenceDataSources();

        if (formDataSourceHasMethod(InventSiteGateEntry_DataSource, identifierStr(reReadLines)))
        {
            InventSiteGateEntry_DataSource.reReadLines();
        }
    }
}

/// <summary>
/// This method acts as edit method for the marked purchase order line.
/// </summary>
/// <param name="_set">
/// Boolean value to set the value.
/// </param>
/// <param name="_line">
/// Common record value to set.
/// </param>
/// <param name="_id">
/// Num value to set.
/// </param>
/// <param name="_lineNum">
/// LineNum value to set.
/// </param>
/// <param name="_qty">
/// Qty value to set.
/// </param>
/// <param name="_inventDimId">
/// InventDimId value to set.
/// </param>
/// <param name="_itemId">
/// ItemId value to set.
/// </param>
/// <param name="_copy">
/// NoYes value to set.
/// </param>
/// <returns>
/// NoYes enum value.
/// </returns>
NoYes editCopy(boolean  _set,
               Common   _line,
               Num  _id,
               LineNum _lineNum,
               Qty _qty,
               InventDimId _inventDimId,
               ItemId _itemId,
               NoYes _copy)
{
    NoYes  copy;
    element.findTmpFrmVirtual(tmpFrmVirtualLines, _line.TableId, _line.RecId);

    if (tmpFrmVirtualLines)
    {
        copy   = NoYes::Yes;
    }

    if (_set)
    {
        if (_copy)
        {
            element.writeTmpFrmVirtual(tmpFrmVirtualLines,
                                       _line.TableId,
                                       _line.RecId,
                                       _id,
                                       _lineNum,
                                       _qty,
                                       _inventDimId,
                                       _itemId
                                       );

            copy   = NoYes::Yes;
        }
        else
        {
            tmpFrmVirtualLines.delete();
            copy   = NoYes::No;
        }
    }

    return copy;
}

/// <summary>
/// Fill marked gate entry lines.
/// </summary>
public void fillMarkedGateEntryLines()
{
    InventSiteGateEntryLine_IN  inventSiteGateEntryLine;

    insert_recordset tmpFrmVirtualLines(
        @TableNum,
        RecordNo,
        Id,
        LineNum,
        Qty,
        InventDimId,
        ItemId)
    select SourceTableId, SourceRecId, OrderId, LineNum, OrderQty, InventDimId, ItemId from inventSiteGateEntryLine
        where inventSiteGateEntryLine.InventSiteGateEntry == element.args().record().RecId;
}

/// <summary>
/// Finds the <C>TmpFrmVirtual</C> record based on Table Id and Record Id.
/// </summary>
/// <param name="_tmpFrmVirtual">
/// A <C>TmpFrmVirtual</C> record.
/// </param>
/// <param name="_tableId">
/// Table Id of <c>PurchLine</c> record.
/// </param>
/// <param name="_recId">
/// Record Id of <c>PurchLine</c> record.
/// </param>
void findTmpFrmVirtual(TmpFrmVirtual _tmpFrmVirtual, TableId _tableId, RecId _recId)
{
    select _tmpFrmVirtual
        where _tmpFrmVirtual.TableNum   == _tableId &&
              _tmpFrmVirtual.RecordNo   == _recId;
}

/// <summary>
/// Performs specific actions when a form is initialized.
/// </summary>
/// <exception cref="Exception::Error">
/// Warehouse must be filled on gate entry.
/// </exception>
/// <exception cref="Exception::Error">
/// Reference party must be filled on gate entry.
/// </exception>
public void init()
{
    inventSiteGateEntry = element.args().record();

    super();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    if (!inventSiteGateEntry.InventLocationId)
    {
        throw error(strFmt("@SYP4860302"));
    }
    else if (!inventSiteGateEntry.ReferencePartyId)
    {
        throw error(strFmt("@SYP4860303"));
    }

    this.fillMarkedGateEntryLines();
}

/// <summary>
/// This method returns the <c>InventDimCtrl_Frm_EditDimensions</c> object.
/// </summary>
/// <returns>
/// <c>InventDimCtrl_Frm_EditDimensions</c> object.
/// </returns>
Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

/// <summary>
/// This method is used for handling the controls based on certain conditions.
/// </summary>
/// <param name="mode">
/// InventDimFormDesignUpdate value.
/// </param>
/// <exception cref="Exception::Error">
/// Incorrect parameter for the function.
/// </exception>
public void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_EditDimensions::newFromForm(element);
            }
            break;

        case InventDimFormDesignUpdate::Active :
        case InventDimFormDesignUpdate::LinkActive :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(Salesline.ItemId));
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(Salesline.ItemId));
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strfmt("@SYS54195",funcname()));
    }
}

/// <summary>
/// This method is used for creating the <c>TmpFrmVirtual</c> record.
/// </summary>
/// <param name="_tmpFrmVirtual">
/// The <c>TmpFrmVirtual</c> record.
/// </param>
/// <param name="_tableId">
/// TableId value to set.
/// </param>
/// <param name="_recId">
/// RecId value to set.
/// </param>
/// <param name="_id">
/// Num value to set.
/// </param>
/// <param name="_lineNum">
/// LineNum value to set.
/// </param>
/// <param name="_qty">
/// Qty value to set.
/// </param>
/// <param name="_inventDimId">
/// InventDimId value to set.
/// </param>
/// <param name="_itemId">
/// ItemId value to set.
/// </param>
void writeTmpFrmVirtual(
    TmpFrmVirtual _tmpFrmVirtual,
    TableId _tableId,
    RecId _recId,
    Num _id,
    LineNum _lineNum,
    Qty _qty,
    InventDimId _inventDimId,
    ItemId _itemId)
{
    _tmpFrmVirtual.TableNum = _tableId;
    _tmpFrmVirtual.RecordNo = _recId;
    _tmpFrmVirtual.Id = _id;
    _tmpFrmVirtual.LineNum = _lineNum;
    _tmpFrmVirtual.Qty = _qty;
    _tmpFrmVirtual.InventDimId = _inventDimId;
    _tmpFrmVirtual.ItemId = _itemId;
    _tmpFrmVirtual.write();
}

/// <summary>
/// Performs specific actions when a <c>SalesLine</c> datasource is executed.
/// </summary>
public void executeQuery()
{
    qbrSalesStatus.value(SysQuery::value(SalesStatus::Backorder));
    qbrCustAccount.value(SysQuery::value(InventSiteGateEntry.ReferencePartyId));

    if (InventSiteGateEntry.ReferenceDocumentType == InventSiteGateRefDocType_IN::SalesReturnOrder)
    {
        qbrSalesType.value(strfmt('((%1 = %2) || (%3 < %4))', fieldstr(SalesLine, SalesType), SysQuery::value(enum2int(SalesType::ReturnItem)), fieldstr(SalesLine, SalesQty), 0));
    }
    else
    {
        qbrSalesType.value(strfmt('((%1 = %2) && (%3 > %4))', fieldstr(SalesLine, SalesType), SysQuery::value(enum2int(SalesType::Sales)), fieldstr(SalesLine, SalesQty), 0));
    }

    super();
}

/// <summary>
/// Performs specific actions when a <c>SalesLine</c> datasource is initialized.
/// </summary>
public void init()
{
    super();

    qbrSalesStatus = this.query().dataSourceTable(tableNum(SalesLine)).addRange(fieldNum(SalesLine, SalesStatus));
    qbrCustAccount = this.query().dataSourceTable(tableNum(SalesLine)).addRange(fieldNum(SalesLine, CustAccount));
    qbrSalesType = this.query().dataSourceTable(tableNum(SalesLine)).addRange(fieldNum(SalesLine, SalesType));
}

