    [Form]
    public final class InventStatistics extends FormRun
    {
        TmpStatPer      tmpStat;
}

void init()
{
    super();

    tmpStat                 = TmpStatPer::createDefault();
    tmpStatPer.setTmpData(tmpStat);
}

