    ctrlPosCorrTotal.realValue(conPeek(getLastValues, 11));

    ctrlSendDate.dateValue(conPeek(getLastValues, 12));
    ctrlSendNum.text(conPeek(getLastValues, 13));
    ctrlSendQty.realValue(conPeek(getLastValues, 14));
    ctrlSendTotal.realValue(conPeek(getLastValues, 15));

