    ctrlqtyAvailable.realValue(conPeek(getLastValues, 24));
    ctrlnumOfNotAvailable.realValue(conPeek(getLastValues, 25));

    TmpStatPer::updateInventStatistics(tmpStat, inventTable);
    tmpStatPer_ds.research();

