public boolean validateDelete()
{
    InventItemPriceSim      inventItemPriceSimCheck;
    InventStdCostConvItem   inventStdCostConvItemCheck;
    boolean                 ret;

    ret = super();

    if (ret)
    {
        // Are items multi-selected
        if (inventStdCostConvItem_ds.getFirst(true))
        {
            // Only ask the first time
            if (checkMultiSelectDeletePrices)
            {
                checkMultiSelectDeletePrices = false;

                select firstonly RecId
                    from inventItemPriceSimCheck
                    where inventItemPriceSimCheck.VersionId == inventStdCostConv.CostingVersionId
                    exists join inventStdCostConvItemCheck
                        where inventStdCostConvItemCheck.ItemId             == inventItemPriceSimCheck.ItemId   &&
                              inventStdCostConvItemCheck.StdCostConvRecId   == inventStdCostConv.RecId;

                if (inventItemPriceSimCheck.RecId)
                {
                    ret = Box::yesNo("@SYS123272", DialogButton::No, "@SYS28002") == DialogButton::Yes;
                }
            }
        }
        else
        {
            select firstonly RecId
                from inventItemPriceSimCheck
                where inventItemPriceSimCheck.VersionId == inventStdCostConv.CostingVersionId   &&
                      inventItemPriceSimCheck.ItemId    == inventStdCostConvItem.ItemId;

            if (inventItemPriceSimCheck.RecId)
            {
                ret = Box::yesNo(strFmt("@SYS123273", inventStdCostConvItem.ItemId), DialogButton::No, "@SYS28002") == DialogButton::Yes;
            }
        }
    }

    return ret;
}

