    // Set up variables for implementing the pack() and unpack() methods.
    WHSInventTransFilter                whsInventTransFilterVal;
    boolean                             skipTransactionFilter;

    #DEFINE.CurrentVersion(1)
    #localMacro.CurrentList
        WHSInventTransFilterVal
    #endMacro

    InventTransReference                inventTransReference;
    Map                                 mapinventTransReference;

}

public void close()
{
    super();
    InventTurnoverReportDetail_RU::onFormClose(element);

    xSysLastValue::saveLast(element);
}

/// <summary>
///    Enables and disables fields for when this form is called from InventTrackingDimTracing form.
/// </summary>
/// <remarks>
///    This is specific to Inventory tracing.
/// </remarks>
private void disableEnableControlsForItemTracing()
{
    inventTrans_ItemId.visible(true);
}

void doResearch()
{
    inventTransOrigin_DS.research();
}

void enableButtons()
{
    inventSettlementTree.enabled(inventTrans.isUpdatedFinancial());
    ledgerTransVoucher_UnpostedInvoices.enabled(inventTrans.hasUnpostedInvoices() == HasUnpostedInvoices::Yes);
    pdsBatchAttributes.enabled(inventDim.InventBatchId != '');
    InventReserve.enabled(InventTransOrigin.ReferenceCategory != InventTransType::WHSWork);
}

void init()
{
    if (!SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]) || element.args().parmObject() is InventTrackingTransactionSummaryFilter)
    {
        traceFilter = element.args().parmObject() as InventTrackingTraceFilter;
    }
    if (traceFilter)
    {
        traceFilter.parmFormRun(this);
    }

    super();

    // When there is no caller and no record passed in the args then isFromMenu will be true.
    boolean isFromMenu = ((element.args().caller() == null) && (element.args().record() == null)) || element.args().menuItemName() == menuItemDisplayStr(InventTransNew);

    // Unpack the last query range values from the WHSInventTransFilter
    // combobox and initialize the form with those values.
    if (element.args().dataset() == tableNum(WHSLicensePlate))
    {
        inventDim_ds.query().dataSourceTable(tableNum(InventDim)).addRange(fieldNum(InventDim,LicensePlateId)).value(element.args().record().(fieldNum(WHSLicensePlate, LicensePlateId)));
    }

    if (isFromMenu)
    {
        Query query = this.addQueryFilterRanges();
        this.updateQueryForMenu(query);
    }

    xSysLastValue::getLast(this);
    whsInventTransFilter.selection(whsInventTransFilterVal);

    InventTrans::formSetCaption(element);
    element.updateDesign(InventDimFormDesignUpdate::Init);

    // <GEERU>
    InventTurnoverReportDetail_RU::onFormInit(element, inventTrans_DS);
    // </GEERU>
    if (traceFilter)
    {
        traceFilter.formInitPost();
        this.disableEnableControlsForItemTracing();
    }

    if (WHSInventoryTransactionConfigurationProvider::isWarehouseInventoryTransactionStackEnabled())
    {
        WHSInventoryTransactionView whsInventoryTransactionView;
        if ((select firstonly RecId from whsInventoryTransactionView).RecId)
        {
            SystemNotificationsWhatsNewManager::AddWhatsNewWithActionLink(
                '{16E11E42-AD4D-4BB1-BB90-42B8BAF63CDC}',
                "@WHSInventory:WHSWarehouseInventoryTransactionFeature_Label",
                strFmt(
                    "@WHSInventory:WHSWarehouseInventoryTansactionNotificationDescription",
                    "@WHSInventory:WHSWarehouseInventoryTransactionFeature_Label",
                    "@SYS1286",
                    "@WHSInventory:WHSWarehouseTransactions_Label",
                    "@SYS438"),
                ActionPane.name(),
                'https://go.microsoft.com/fwlink/?linkid=2175071');
        }
    }
}

