public container pack()
{
    return [#CurrentVersion, #CurrentList];
}

void setInventTransReference(InventTrans _inventTrans)
{
    RefRecId   key = _inventTrans.RecId;

    if (!mapinventTransReference)
    {
        mapinventTransReference = new Map(Types::Int64, Types::Class);
    }

    if (mapinventTransReference.exists(key))
    {
        inventTransReference = mapinventTransReference.lookup(key);
    }
    else
    {
        inventTransReference = InventTransReference::newFromInventTrans(_inventTrans);
        mapinventTransReference.insert(key, inventTransReference);
    }
}

public boolean unpack(container _packedClass)
{
    boolean     ret;
    int         version;

    version = conPeek(_packedClass, 1);

    switch (version)
    {
        case #CurrentVersion:
            [version, #CurrentList] = _packedClass;
            ret = true;
            break;
    }

    return ret;
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParm;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init          :
            InventDimSetupGrid::initInventDimParmInventTrans(inventDimParm);

            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_ActiveRightClick::newFromForm(element);
            }
            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParm);
            inventDimFormSetup.formSetControls(true);
    }
}

internal void openedFromTile()
{
    super();

    skipTransactionFilter = true;
}

private void updateQueryForMenu(Query _query)
{
    QueryBuildDataSource ds;
    TableId sourceTable;

    sourceTable = element.args().dataset();
    Debug::assert(sourceTable != tableNum(InventTransOrigin));

    element.promptUser(_query);
}

private void promptUser(Query _query)
{
    QueryRun queryRun = new QueryRun(_query);

    if (!queryRun.prompt())
    {
        element.close();
    }

    InventTransOrigin_ds.query(queryRun.query());
}

protected Query addQueryFilterRanges()
{
    Query query = InventTransOrigin_DS.query();

    QueryBuildDataSource inventTransOriginQbds = query.dataSourceTable(tableNum(InventTransOrigin));
    inventTransOriginQbds.addRange(fieldNum(InventTransOrigin, ItemId));
    inventTransOriginQbds.addRange(fieldNum(InventTransOrigin, ReferenceCategory));
    inventTransOriginQbds.addRange(fieldNum(InventTransOrigin, ReferenceId));

    QueryBuildDataSource inventDimQbds = InventDim_DS.query().dataSourceName(InventDim_DS.name());
    SysQuery::findOrCreateRange(inventDimQbds, fieldNum(InventDim, InventSiteId));
    SysQuery::findOrCreateRange(inventDimQbds, fieldNum(InventDim, InventLocationId));
    SysQuery::findOrCreateRange(inventDimQbds, fieldNum(InventDim, inventSerialId));
    SysQuery::findOrCreateRange(inventDimQbds, fieldNum(InventDim, inventBatchId));

    return query;
}

internal void initTraceFilter(InventTrackingTraceFilter _traceFilter)
{
    _traceFilter.parmMainDataSource(inventTrans_ds);
    _traceFilter.parmFilterDataSource(inventTransTmpFilter_ds);
    _traceFilter.dataSourceInitPost();
}

void init()
{
    super();

    InventTransFormHelper::formQueryAddDynalink(inventTransOrigin_DS,inventTrans_DS,element.args());

    if (traceFilter)
    {
        this.initTraceFilter(traceFilter);
    }

    // Do not cache display methods more than once
    if (!hasCachedDisplayMethods)
    {
        inventTrans_ds.cacheAddMethod(tableMethodStr(InventTrans, unpostedInvoiceId));
        inventTrans_ds.cacheAddMethod(tableMethodStr(InventTrans, isNonFinancialTransfer));
        inventTrans_ds.cacheAddMethod(tableMethodStr(InventTrans, transferInventTransId));
        hasCachedDisplayMethods = true;
    }
}

int active()
{
    int ret;

    ret = super();

    inventDim_DS.active();
    element.enableButtons();

    return ret;
}

