    int ret;

    ret = super();
    inventTrackingDimTracing.enabled(InventDim.canPerformItemTracing());

    return ret;
}

void executeQuery()
{
    Query                   query;
    QueryBuildDataSource    qbds;
    QueryFilter             queryFilter;

    InventTransFormHelper::formQueryPreExecute(inventTransOrigin_DS, inventTrans_ds, element.args());

    query = inventTransOrigin_ds.query();
    qbds = query.dataSourceTable(tableNum(InventTransOrigin));

    if (!skipTransactionFilter)
    {
        switch (whsInventTransFilterVal)
        {
            case WHSInventTransFilter::DisplayAll:
                if (query.findQueryFilter(qbds, fieldStr(InventTransOrigin, ReferenceCategory)))
                {
                    query.clearQueryFilters(qbds, fieldStr(InventTransOrigin, ReferenceCategory));
                }
                break;

            case WHSInventTransFilter::OmitWork:
                queryFilter = SysQuery::findOrCreateQueryFilter(query, qbds, fieldStr(InventTransOrigin, ReferenceCategory));
                queryFilter.value(SysQuery::valueNot(InventTransType::WHSWork));
                break;

            case WHSInventTransFilter::WorkOnly:
                queryFilter = SysQuery::findOrCreateQueryFilter(query, qbds, fieldStr(InventTransOrigin, ReferenceCategory));
                queryFilter.value(queryValue(InventTransType::WHSWork));
            break;
        }
    }

    super();
}

int active()
