}

void  clicked()
{
    ledgerTransVoucher_UnpostedInvoices.enabled(inventTrans.hasUnpostedInvoices() == HasUnpostedInvoices::Yes);
    super();
}

public boolean modified()
{
    WHSInventTransFilter    whsInventTransFilterEnum;
    boolean                 ret;
    WHSInventTransFilter    originInventTransFilter = whsInventTransFilterVal;

    ret = super();

    whsInventTransFilterVal = str2enum(whsInventTransFilterEnum, whsInventTransFilter.valueStr());

    if (skipTransactionFilter
        && originInventTransFilter != whsInventTransFilterVal)
    {
        skipTransactionFilter = false;
    }
    inventTransOrigin_ds.executeQuery();

    return ret;
}
