public boolean canClose()
{
    boolean ret;

    if (tmpinventTransWMS.RecId)
    {
        if (Box::yesNo("@SYS54895",DialogButton::Yes) != DialogButton::Yes)
        {
            return false;
        }
    }

    ret = super();

    return ret;
}

public void close()
{
    Object callerForm;
    if (element.args().caller())
    {
        callerForm = element.args().caller();
        if (callerForm is FormRun && formHasMethod(callerForm,identifierStr(doResearch)))
        {
            callerForm.doResearch();
        }
    }

    super();
}

public void doResearch()
{
    element.doRefresh();

    ctrlTransactions.setFocus();
}

public void enableControls()
{
    InventTrans inventTransLocal;
    boolean     enabledAddPickingListUpdate = true;

    ctrlReservationButton.enabled(inventTrans.RecId != 0 && !inventTransWMS_Pick.isInventTransSelected(inventTrans.RecId));
    ctrlSplitButton.enabled(inventTrans.RecId != 0 && !inventTransWMS_Pick.isInventTransSelected(inventTrans.RecId));
    ctrlInventDimParmFixedInventDim.enabled(inventTrans.RecId != 0);

    tmpInventTransWMS_ds.allowCreate(inventTrans.RecId != 0);
    ctrlAdd.enabled(tmpInventTransWMS_ds.allowCreate());
    ctrlUpdate.enabled(tmpInventTransWMS.RecId != 0);
    ctrlDelete.enabled(tmpInventTransWMS.RecId != 0);
    ctrlInventDimParmFixedTmpInventDim.enabled(tmpInventTransWMS.RecId != 0);

    tmpInventTransWMS_ds.object(fieldNum(TmpInventTransWMS,ItemId)).allowEdit(tmpInventTransWMS.RecId == 0);

    for (inventTransLocal = inventTrans_ds.getFirst(true) ? inventTrans_ds.getFirst(true) : inventTrans_ds.cursor();
         inventTransLocal;
         inventTransLocal = inventTrans_ds.getNext())
    {
        if (inventTransWMS_Pick.isInventTransSelected(inventTransLocal.RecId))
        {
            enabledAddPickingListUpdate = false;
            break;
        }
    }

    ctrlAddPickingListUpdate.enabled(enabledAddPickingListUpdate);
}

public void init()
{
    if (!element.args().dataset())
    {
        throw error(strFmt("@SYS22678",element.name()));
    }

    var noWhsProcessCheck = WHSNoPickingProcessCheck::newFromCommon(element.args().record());
    if (!noWhsProcessCheck.run())
    {
        throw error(noWhsProcessCheck.failureMessage());
    }

    super();

    countryRegion_RU    = SysCountryRegionCode::isLegalEntityInCountryRegion([#IsoRU]);

    inventTransWMS_Pick = InventTransWMS_Pick::newStandard(tmpInventTransWMS,inventTrans_ds.query());

    element.enableControls();
}

