/// <summary>
///     Updates the quantity for the picking line when the item is full-visibility catch-weight.
/// </summary>
/// <param name="_tmpInventTransWMS">
///     The selected picking line.
/// </param>
/// <param name="_tmpinventDim">
///     The dimensions associated with the picking line.
/// </param>
public void pdsCWLineDimSerialModified(
    TmpInventTransWMS   _tmpInventTransWMS,
    InventDim           _tmpinventDim)
{
    if (_tmpinventDim.InventSerialId && PdsGlobal::pdsIsCWFullVisItem(_tmpInventTransWMS.ItemId))
    {
        _tmpInventTransWMS.PdsCWQty = 1;
        element.pdsCWQtyItemModified(_tmpInventTransWMS, _tmpinventDim);
    }
}

/// <summary>
///     Updates the inventory quantity when the CW quantity is changed on the picking line.
/// </summary>
/// <param name="_tmpInventTransWMS">
///     The line that was modified.
/// </param>
/// <param name="_tmpinventDim">
///     The dimensions associated with the line that was modified.
/// </param>
public void pdsCWQtyItemModified(
    TmpInventTransWMS   _tmpInventTransWMS,
    InventDim           _tmpinventDim)
{
    InventDimGroupSetup fvCWItemDimSetup;
    InventDimParm       inventDimParm;
    InventOnHandQty        inventOnhandForSerial;
    InventQty           onHandForSerial;
    InventQty           reserveQty;

    if (PdsGlobal::pdsIsCWItem(inventTrans.ItemId))
    {
        reserveQty = PdsCatchWeight::inventQty(inventTrans.ItemId, tmpInventTransWMS.PdsCWQty);

        if (    tmpinventDim.InventSerialId
            &&  abs(tmpInventTransWMS.PdsCWQty) == 1
            &&  PdsGlobal::pdsIsCWFullVisItem(tmpInventTransWMS.ItemId))
        {
            fvCWItemDimSetup  = InventDimGroupSetup::newItemId(tmpInventTransWMS.ItemId);
            fvCWItemDimSetup.inventDimParmActive(inventDimParm);

            inventOnhandForSerial = InventOnHandQty::newItemDim(
                    tmpInventTransWMS.ItemId,
                    InventDim::findOrCreate(_tmpinventDim),
                    inventDimParm);

            onHandForSerial = inventOnhandForSerial.availReservation(NoYes::Yes);

            if (    onHandForSerial
                &&  PdsCatchWeightItem::pdsCWIsValidQty(
                        tmpInventTransWMS.ItemId,
                        abs(onHandForSerial),
                        1))
            {
                reserveQty = onHandForSerial;
            }
        }
        _tmpInventTransWMS.InventQty = reserveQty;
        tmpInventTransWMS_ds.refresh();
    }
}

