public void updateDesign(
    InventDimFormDesignUpdate _mode,
    IdentifierName            _datasourceName)
{
    InventDimParm           inventDimParm;
    InventDimParm           inventDimParmConsistent;
    InventDimParm           inventDimParmDisabled;
    InventDimParm           inventDimParmVisibleGrid;
    InventDimGroupSetup     inventDimGroupSetup;
    InventDimParm           inventdimparmtest;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
        case InventDimFormDesignUpdate::Active:
        case InventDimFormDesignUpdate::FieldChange:
            inventDimParmVisibleGrid.initFromInventDimParm(inventDimParmAllTransactions);
            if (   !inventDimFormSetupInventDim
                || _mode == InventDimFormDesignUpdate::Init)
            {
                inventDimFormSetupInventDim = InventDimCtrl::newFromFormValidDataSource(element,inventDim_ds.name());
                inventDimFormSetupInventDim.parmDimParmVisibleGrid(inventDimParmVisibleGrid);
                inventDimFormSetupInventDim.parmDimParmEnabled(inventDimParmDisabled);
                inventDimFormSetupInventDim.formSetControls(true,true);
            }
            if (   !inventDimFormSetupTmpInventDim
                || _mode == InventDimFormDesignUpdate::Init)
            {
                inventDimFormSetupTmpInventDim = InventDimCtrl::newFromFormValidDataSource(element,tmpInventDim_ds.name());
                inventDimFormSetupTmpInventDim.parmDimParmVisibleGrid(inventDimParmVisibleGrid);
                inventDimFormSetupTmpInventDim.parmDimParmEnabled(inventDimParmVisibleGrid);
                inventDimFormSetupTmpInventDim.formSetControls(true,true);
            }

            switch (_datasourceName)
            {
                case tmpInventTransWMS_ds.name():
                    inventDimGroupSetup = InventDimGroupSetup::newItemId(tmpInventTransWMS.ItemId);
                    inventDimFormSetupTmpInventDim.formActiveSetup(inventDimGroupSetup);
                    inventDimGroupSetup.inventDimParmActive(inventDimParm);
                    if (tmpInventTransWMS.RecId)
                    {
                        inventDimParmConsistent.initConsistentDimAllGrp();
                        inventDimParmConsistent.toggleAllInventDim();
                        inventDimParm = InventDimParm::andParms(inventDimParmConsistent,inventDimParm);
                    }
                    inventdimparmtest.setAllInventDim();
                    inventDimFormSetupTmpInventDim.parmDimParmVisibleGrid(inventDimParmVisibleGrid);
                    inventDimFormSetupTmpInventDim.parmDimParmEnabled(inventDimParm);
                    this.inventDimFormSetupPreSetControls();
                    inventDimFormSetupTmpInventDim.formSetControls(true);
                    break;
            }
            break;
    }

    element.enableControls();
}

protected void inventDimFormSetupPreSetControls()
{
    this.allowEditingInventoryOwner();
}

private void allowEditingInventoryOwner()
{
    // Picking form is shared between different processes. Check the inventory movement to determine
    // if inventory owner is supposed to be editable in the specific process.
    InventDimParm canBeEnabledInventDimParm = inventDimFormSetupTmpInventDim.parmDimParmCanBeEnabled();
    if (inventTrans && !inventTrans.inventMovement().hasMandatoryInventoryOwner())
    {
        canBeEnabledInventDimParm.InventOwnerIdFlag_RU = true;
    }

    inventDimFormSetupTmpInventDim.parmDimParmCanBeEnabled(canBeEnabledInventDimParm);
}

private boolean isProcessEnabledForVendorOwnedInventory(InventTransType _inventTransType)
{
    return (_inventTransType == InventTransType::ProdLine
        && isConfigurationKeyEnabled(configurationKeyNum(ConsignmentInbound)));
}

/// <summary>
///     Updates inventDimId in inventTrans setting inventProfileId_RU field
///     from reference kanban
/// </summary>
/// <param name="_inventTrans">
///     InventTrans record for update
/// </param>
public void updateInventDimId_RU(InventTrans _inventTrans)
{
    InventDim                       transInventDim;
    InventTransOrigin               inventTransOrigin = InventTransOrigin::find(_inventTrans.InventTransOrigin);
    Kanban                          kanban            = Kanban::findKanbanId(inventTransOrigin.ReferenceId);
    BOM                             bom;

    if (kanban)
    {
        transInventDim                      = _inventTrans.inventDim();

        if (kanban.Type == LeanKanbanType::Manufacturing
            && kanban.ItemId != _inventTrans.ItemId)
        {
            select firstonly bom
                where bom.bomId == kanban.bomId
                        && bom.ItemId == _inventTrans.ItemId;
            transInventDim.InventProfileId_RU   = bom.inventDim().InventProfileId_RU;
        }
        else
        {
            transInventDim.InventProfileId_RU   = kanban.inventDim().InventProfileId_RU;
        }

        transInventDim                      = InventDim::findOrCreate(transInventDim);
        _inventTrans.InventDimId            = transInventDim.InventDimId;
    }
}

