public boolean validate()
{
    boolean ret;

    if (element.mustCheckExpiryDate())
    {
        Common common = element.args().record();
        CustAccount custId;

        switch (common.TableId)
        {
            case tableNum(SalesLine) :
                custId = common.(fieldNum(SalesLine,CustAccount));
                break;
            case tableNum(SalesParmLine) :
                custId = common.(fieldNum(SalesParmLine,CustAccount));
                break;
            default:
                custId = '';
        }

        ret = InventBatch::pdsCheckExpiryDate(inventdim.InventBatchId,
                                              inventTrans.ItemId,
                                              checkDate,
                                              true,
                                              custId,
                                              overrideFEFO);

        if (ret)
        {
            ret = super();
        }

        if (ret)
        {
            PdsBatchDispCheck_PickList pdsCheckPick = PdsBatchDispCheck_PickList::construct();
            ret = pdsCheckPick.validateTransCheck(inventTrans,
                                                  element.args().record(),
                                                  inventDim.InventBatchId);
        }
    }
    else
    {
        ret = super();
    }

    return ret;
}

/// <summary>
///     Validates a record change in <c>TmpInventTransWMS</c> data source.
/// </summary>
/// <returns>
///     The result of validation.
/// </returns>
public boolean validateWrite()
{
    boolean ret = super();

    ret = PdsCatchWeight::validateInventQty(tmpInventTransWMS.ItemId, tmpInventTransWMS.InventQty, tmpInventTransWMS.PdsCWQty) && ret;

    if (tmpInventTransWMS.InventQty * tmpInventTransWMS.orig().InventQty < 0)
    {
        ret = checkFailed(strFmt("@SYS324605", tmpInventTransWMS.orig().InventQty));
    }

    if (tmpInventTransWMS.PdsCWQty * tmpInventTransWMS.orig().PdsCWQty < 0)
    {
        ret = checkFailed(strFmt("@SYS324605", tmpInventTransWMS.orig().PdsCWQty));
    }

    return ret;
}

public void write()
{
    boolean result;

    tmpInventTransWMS.InventDimId = InventDim::findOrCreate(tmpinventDim).InventDimId;

    result = inventTransWMS_Pick.writeTmpInventTransWMS(tmpInventTransWMS);

    tmpinventDim.data(InventDim::find(tmpInventTransWMS.InventDimId));
    tmpInventDim_ds.setCurrent();

    if (result)
    {
        tmpInventTransWMS_ds.reread();
        inventTrans_ds.research(true);
    }
}

