public int active()
{
    int ret = super();

    if (#PdsShelfEnabled)
    {
        switch (inventTransOriginMovement.ReferenceCategory)
        {
            case InventTransType::Sales :
                SalesLine salesLine = inventTransOriginMovement.inventMovement().buffer() as SalesLine;
                checkDate = salesLine.ReceiptDateRequested;
                break;
            case InventTransType::Production :
                ProdTable prodTable = inventTransOriginMovement.inventMovement().buffer() as ProdTable;
                checkDate = prodTable.DlvDate;
                break;
            case InventTransType::ProdLine :
                ProdBOM prodBOM = inventTransOriginMovement.inventMovement().buffer() as ProdBOM;
                checkDate = prodBOM.prodTable().DlvDate;
                break;
            case InventTransType::TransferOrderShip :
                InventTransferLine inventTransferLine = inventTransOriginMovement.inventMovement().buffer() as InventTransferLine;
                checkDate = inventTransferLine.ReceiveDate;
                break;
            default :
                checkDate = inventTrans.ShippingDateRequested;
        }

        overrideFEFO = inventTransOriginMovement.inventMovement().pdsOverrideFEFO();
    }

    return ret;
}

public void clicked()
{
    InventTrans             inventTransLocal;

    super();

    //loop selected records
    for (inventTransLocal = inventTrans_ds.getFirst(true) ? inventTrans_ds.getFirst(true) : inventTrans_ds.cursor();
         inventTransLocal;
         inventTransLocal = inventTrans_ds.getNext())
    {
        if (countryRegion_RU)
        {
            element.updateInventDimId_RU(inventTransLocal);
        }

        inventTransWMS_Pick.createFromInventTrans(inventTransLocal);
    }

    tmpInventTransWMS_DS.executeQuery();
    inventTrans_ds.research(true);
}

