public void clicked()
{
    boolean ok = true;

    if (element.mustCheckExpiryDate())
    {
        TmpInventTransWMS   inventTransWMS;
        InventDim           inventDimPick;
        inventTransWMS.setTmpData(tmpInventTransWMS);

        while select RecId from inventTransWMS
            join inventBatchId from inventDimPick
                where inventDimPick.InventDimId == inventTransWMS.InventDimId
                    && inventDimPick.inventBatchId
        {
            if (!InventBatch::pdsCheckExpiryDate(inventDimPick.inventBatchId, inventTrans.ItemId, checkDate, true, '', overrideFEFO))
            {
                ok = ok && checkFailed(strFmt("@SCM:CantPickExpiredBatchMessage", inventDimPick.InventBatchId, inventTrans.ItemId));
            }
        }
    }

    if (ok)
    {
        inventTransWMS_Pick.updateInvent();
        tmpInventTransWMS_DS.executeQuery();
        element.doResearch();
    }

    super();
}

public boolean modified()
{
    boolean result;

    result = super();
    element.updateDesign(InventDimFormDesignUpdate::FieldChange,tmpInventTransWMS_ds.name());

    return result;
}
