Method.Source:
    [Form]
    public class InventValueReportSetup extends FormRun
    {
        FormStringControl                       dimensionFocusControl;
}

public void init()
{
    super();

    InventValueReport::checkFixReferenceAll();

    element.initControls();

    CompareToLedger_InventoryLedgerDimension.parmPostingType(LedgerPostingType::None);
    CompareToLedger_InventoryLedgerDimension.parmIncludeTotalAccounts(true);

    CompareToLedger_WIPLedgerDimension.parmPostingType(LedgerPostingType::None);
    CompareToLedger_WIPLedgerDimension.parmIncludeTotalAccounts(true);

    CompareToLedger_DeferredCOGSLedgerDimension.parmPostingType(LedgerPostingType::None);
    CompareToLedger_DeferredCOGSLedgerDimension.parmIncludeTotalAccounts(true);

    CompareToLedger_COGSLedgerDimension.parmPostingType(LedgerPostingType::None);
    CompareToLedger_COGSLedgerDimension.parmIncludeTotalAccounts(true);

    dimensionFocusControl = element.design().controlName('Dimension_DimensionFocus_Description');
}

public void initControls()
{
    boolean allowEditLedgerDimension;
    boolean dateRangeTransDate;

    // Inventory
    dateRangeTransDate = inventValueReport.SelectDateRange == InventValueReportSelectDateRange::TransDate;
    allowEditLedgerDimension = inventValueReport.ViewInventory && inventValueReport.PrintLedgerValue && dateRangeTransDate;
    compareToLedger_InventoryLedgerDimension.enabled(allowEditLedgerDimension);

    // WIP
    allowEditLedgerDimension = inventValueReport.ViewWIP && inventValueReport.PrintLedgerValue;
    compareToLedger_WIPLedgerDimension.enabled(allowEditLedgerDimension);

    // Deferred COGS
    allowEditLedgerDimension = inventValueReport.ViewDeferredCOGS && inventValueReport.PrintLedgerValue;
    compareToLedger_DeferredCOGSLedgerDimension.enabled(allowEditLedgerDimension);

    // COGS
    allowEditLedgerDimension = inventValueReport.ViewCOGS && inventValueReport.PrintLedgerValue;
    compareToLedger_COGSLedgerDimension.enabled(allowEditLedgerDimension);

    // Summarize physical and financial values
    inventValueReport_ds.object(fieldNum(InventValueReport, SummarizePhysFinValues)).allowEdit(!inventValueReport.PrintTotalQtyValue);

    // Total quantity and value
    inventValueReport_ds.object(fieldNum(InventValueReport, PrintTotalQtyValue)).allowEdit(!inventValueReport.SummarizePhysFinValues);

    // Average unit cost
    inventValueReport_ds.object(fieldNum(InventValueReport, CalcAvgUnitCost)).allowEdit(inventValueReport.ViewInventory);

    // Detail level
    inventValueReport_ds.object(fieldNum(InventValueReport, IncludeBeginningBalance)).allowEdit(inventValueReport.DetailLevel == TotalTrans::Trans);

    inventValueReport_ds.object(fieldNum(InventValueReport, PrintLedgerValue)).allowEdit(dateRangeTransDate);
    inventValueReport_ds.object(fieldNum(InventValueReport, ViewLabor)).allowEdit(dateRangeTransDate);
    inventValueReport_ds.object(fieldNum(InventValueReport, ViewIndirect)).allowEdit(dateRangeTransDate);
    inventValueReport_ds.object(fieldNum(InventValueReport, ViewDirectOutsourcing)).allowEdit(dateRangeTransDate);
}

public void delete()
{
    super();

    reportSetupTab.enabled(false);
    copyButton.enabled(false);
}

public int active()
{
    int ret;

    ret = super();

    reportSetupTab.enabled(inventValueReport.RecId != 0);
    copyButton.enabled(inventValueReport.RecId != 0);

    if (!inventValueReport.DimensionFocus)
    {
        compareToLedger_PrintLedgerValue.allowEdit(false);
        compareToLedger_PrintLedgerValue.value(NoYes::No);
        inventValueReport.PrintLedgerValue = NoYes::No;
    }
    else
    {
        compareToLedger_PrintLedgerValue.allowEdit(true);
    }

    element.initControls();

    return ret;
}

public void modified()
{
    super();
    element.initControls();

    if (InventValueReportStorageFeature::isEnabled() && InventValueReport.CalcAvgUnitCost == NoYes::Yes && inventValueReport.DetailLevel == TotalTrans::Trans)
    {
        Box::infoOnce("@SYS26676", "@SCM:InventValueReportSetupAverageUnitCostInfo", formStr(InventValueReportSetup));
    }
}

public void modified()
{
    super();
    element.initControls();

    if (inventValueReport.DetailLevel == TotalTrans::Trans)
    {
        Box::infoOnce(
            "@SYS26676",
            "@SCM:RecommendUseInventoryValueReportStorageToAvoidLargePrinting",
            formStr(InventValueReportSetup));
        if (InventValueReportStorageFeature::isEnabled())
        {
            Box::infoOnce("@SYS26676", "@SCM:InventValueReportStorageTransactionWarning", formStr(InventValueReportSetup));
            if(InventValueReport.CalcAvgUnitCost == NoYes::Yes)
            {
                Box::infoOnce("@SYS26676", "@SCM:InventValueReportSetupAverageUnitCostInfo", formStr(InventValueReportSetup));
            }
        }
    }
}

public void modified()
{
    if (dimensionFocusControl.valueStr() == '')
    {
        compareToLedger_PrintLedgerValue.allowEdit(false);
        compareToLedger_PrintLedgerValue.value(NoYes::No);
        inventValueReport.PrintLedgerValue = NoYes::No;
    }
    else
    {
        compareToLedger_PrintLedgerValue.allowEdit(true);
    }
    super();
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    DimensionHierarchy      selectedRecord;
    Query                   lookupQuery;
    QueryBuildDataSource    qbdsDimensionHierarchy;
    QueryBuildDataSource    qbdsDimensionHierarchyLevel;
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(DimensionHierarchy),
                                                                                    _formReferenceControl, true);

    // Display the Title and Department fields in the lookup form.
    sysTableLookup.addLookupfield(fieldNum(DimensionHierarchy, Name));
    sysTableLookup.addLookupfield(fieldNum(DimensionHierarchy, Description));

    // Create a custom Query that filters and displays just dimension sets with main accounts included
    lookupQuery = new Query();
    qbdsDimensionHierarchy = lookupQuery.addDataSource(tableNum(DimensionHierarchy));
    qbdsDimensionHierarchy.addRange(fieldNum(DimensionHierarchy, StructureType)).value(queryValue(DimensionHierarchyType::Focus));
    qbdsDimensionHierarchy.addRange(fieldNum(DimensionHierarchy, DeletedVersion)).value(queryValue(0));
    qbdsDimensionHierarchyLevel = qbdsDimensionHierarchy.addDataSource(tableNum(DimensionHierarchyLevel));
    qbdsDimensionHierarchyLevel.addLink(fieldNum(DimensionHierarchy, RecId), fieldNum(DimensionHierarchyLevel, DimensionHierarchy));
    qbdsDimensionHierarchyLevel.addRange(fieldNum(DimensionHierarchyLevel, DimensionAttribute)).value(queryValue(DimensionAttribute::getWellKnownDimensionAttribute(DimensionAttributeType::MainAccount)));

    sysTableLookup.parmQuery(lookupQuery);

    selectedRecord = sysTableLookup.performFormLookup();

    return selectedRecord;
}

void clicked()
{
    Dialog copyDialog;
    DialogField idField;
    DialogField nameField;
    DictField   dictField;

    InventValueReport inventValueReportNew;

    super();

    copyDialog = new Dialog("@SYS315886");
    // The InventValueReportId EDT is not used for the idField as it will enforce the
    // new id to exist in the InventValueReport table, which is undesired when a new id has to be created.
    dictField = new DictField(tableNum(InventValueReport), fieldNum(InventValueReport, ReportId));
    idField = copyDialog.addField(extendedTypeStr(IdentifierName), dictField.label(), dictField.help());
    idField.displayLength(dictField.displayLength());
    nameField = copyDialog.addField(extendedTypeStr(InventValueReportName));

    if (copyDialog.run())
    {
        inventValueReportNew = inventValueReport.copy(idField.value(), nameField.value());

        if (inventValueReportNew.RecId != 0)
        {
            element.args().lookupRecord(inventValueReportNew);
            inventValueReport_ds.research(false);
        }
    }
}
