[Hookable(false)]
protected date parmViewDate(date _viewDate = viewDate)
{
    viewDate = _viewDate; 
    return viewDate;
}

private boolean allowApproveAndTransfer()
{
    boolean ret = this.loadWorkflowConfiguration() == null;

    if (!ret)
    {
        // A workflow configuration exist. Check status
        ret = jmgStampJournalTable.WorkflowStatus == JmgDaysTotalWorkflowStatus::Completed;
    }

    return ret;
}

public boolean canSubmitToWorkflow()
{
    return jmgStampJournalTable.canSubmitToWorkflow();
}

public void close()
{
    ttsbegin;
    delete_from jmgStampJournalTable
        where jmgStampJournalTable.Calculated   == NoYes::No  &&
              jmgStampJournalTable.ProfileDate  >  DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
    ttscommit;

    super();
}

public void enableDisableFields()
{
    boolean allowApproveAndTransfer = this.allowApproveAndTransfer();

    jmgOvertimeSlize.enabled(!jmgStampJournalTable.Approved);
    jmgPayAddTrans.enabled(!jmgStampJournalTable.Approved);
    jmgRestoreJournalLines.enabled(!jmgStampJournalTable.Calculated && !jmgStampJournalTable.Locked);

    jmgStampJournalTable_DS.allowDelete(!jmgStampJournalTable.Calculated);
    jmgStampJournalTrans_DS.allowEdit(!jmgStampJournalTable.Calculated);
    jmgStampJournalTrans_DS.allowCreate(!jmgStampJournalTable.Calculated);
    jmgStampJournalTrans_DS.allowDelete(!jmgStampJournalTable.Calculated);
    jmgStampjournalTrans_Absence_DS.allowCreate(!jmgStampJournalTable.Calculated);
    jmgStampjournalTrans_Absence_DS.allowDelete(!jmgStampJournalTable.Calculated);

    jmgStampJournalTable_Profile.allowEdit(!jmgStampJournalTable.Calculated);
    identificationGroup.allowEdit(!jmgStampJournalTable.Calculated);
    profile.allowEdit(!jmgStampJournalTable.Calculated);

    jmgStampJournalTrans_Absence_StartTime.allowEdit(!jmgStampJournalTable.Calculated);
    jmgStampJournalTrans_Absence_StopTime.allowEdit(!jmgStampJournalTable.Calculated);
    jmgStampJournalTrans_Absence_JobRef.allowEdit(!jmgStampJournalTable.Calculated);
    jmgStampJournalTrans_Absence_JobId.allowEdit(!jmgStampJournalTable.Approved);
    jmgStampJournalTrans_Absence_AbsencePayQty.allowEdit(!jmgStampJournalTable.Approved);

    jmgStampJournalTable_PayAgreement.allowEdit(jmgStampJournalTable.Calculated && !jmgStampJournalTable.Approved);

    jmgPayAgreementOverride.enabled(jmgStampJournalTable.Calculated);
    menuItemButton.enabled(jmgStampJournalTable.Calculated == NoYes::No);

    if (mode == JmgCalcApproveMode::Approve)
    {
        jmgApproveDate.enabled(allowApproveAndTransfer);
        jmgTransferDate.enabled(allowApproveAndTransfer);

        ApproveSelectedBtn.enabled(allowApproveAndTransfer);
        TransferSelectedBtn.enabled(allowApproveAndTransfer);
    }

    TabFinancialDimensions.allowEdit(
        (JmgStampJournalTrans) &&
        (jmgStampJournalTrans.JourRegType != JmgJourRegTypeEnum::Absence) &&
        (jmgStampJournalTrans.JourRegType != JmgJourRegTypeEnum::IndActivity));
}

void init()
{
    JmgStampJournalTable    inStampJournalTable;

    viewDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()) - 1;
    filterError = NoYes::No;

    super();

    jmgCalcApproveForm  = JmgCalcApproveForm::newFromForm(this);
    jmgCalcApproveForm.setDatasources(jmgStampJournalTable);

    rangeProfileDate = jmgStampJournalTable_ds.query().dataSourceNo(1).addRange(fieldNum(JmgStampJournalTable,ProfileDate));
    rangeError       = jmgStampJournalTable_ds.query().dataSourceNo(1).addRange(fieldNum(JmgStampJournalTable,ContainsError));
    rangeWorker       = jmgStampJournalTable_ds.query().dataSourceNo(1).addRange(fieldNum(JmgStampJournalTable,Worker));
    rangeCalcGroupWorker = SysQuery::findOrCreateRange(jmgEmployee_DS.queryBuildDataSource(), fieldNum(JmgEmployee, CalculateGroupId));
    rangeApprGroupWorker = SysQuery::findOrCreateRange(jmgEmployee_DS.queryBuildDataSource(), fieldNum(JmgEmployee, ApproveGroupId));
    rangeActiveDatesWorker = SysQuery::findOrCreateRange(jmgEmployee_DS.queryBuildDataSource(), fieldNum(JmgEmployee, ActiveFrom));

    // Select only active employees or who have registered hours
    qbdsJmgStampJournalTrans = JmgEmployee::addQueryRangeForActiveOrTimeRegisteredEmployees(jmgEmployee_DS.queryBuildDataSource());
    rangeInactiveWorkerProfileDates = SysQuery::findOrCreateRange(qbdsJmgStampJournalTrans, fieldNum(JmgStampJournalTrans, ProfileDate));

    rangeWorker.enabled(false);
    rangeCalcGroupWorker.enabled(false);
    rangeApprGroupWorker.enabled(false);

    rangeAbsence = jmgStampJournalTrans_Absence_ds.query().dataSourceNo(1).addRange(fieldNum(JmgStampJournalTrans,StampType));
    rangeAbsence.value(queryValue(JmgStampTypeEnum::Absence));

    jmgStampJournalTrans_DS.query().dataSourceTable(tableNum(JmgStampJournalTrans)).addRange(fieldNum(JmgStampJournalTrans,StampType)).value(SysQuery::valueNot(JmgStampTypeEnum::EventCode));

    xSysLastValue::getLast(this);

    // Get parameters from calling dialog.
    if (this.args().parmObject() && this.args().parmObject() is JmgCalcApproveFormParm)
    {
        JmgCalcApproveFormParm calcApproveParm = this.args().parmObject() as JmgCalcApproveFormParm;
        this.initFromJmgCalcApproveFormParm(calcApproveParm);
    }

    // Check if there is initial dataset.
    if (this.args().dataset() == tableNum(JmgStampJournalTable))
    {
        inStampJournalTable = this.args().record();

        viewDate            = inStampJournalTable.ProfileDate;
        jmgGroupCalcId      = inStampJournalTable.CalculateGroupId;
        jmgGroupApproveId   = inStampJournalTable.ApproveGroupId;
    }

    if (mode == JmgCalcApproveMode::Calculate)
    {
        jmgApproveDate.visible(false);
        jmgTransferDate.visible(false);
        jmgPayAddTrans.visible(false);
        ApproveSelectedBtn.visible(false);
        TransferSelectedBtn.visible(false);
        jmgPayAgreementOverride.visible(false);
        overridePay.visible(false);
        inquiry.visible(false);
        approveGroup.visible(false);
        jmgStampJournalTable_PayAgreement.visible(false);

        // Add calculation group range to query.
        rangeGroupCalcId    = jmgStampJournalTable_ds.query().dataSourceNo(1).addRange(fieldNum(JmgStampJournalTable,CalculateGroupId));
        rangeGroupCalcId.value(queryValue(jmgGroupCalcId));
        JmgStampJournalTable::makeLines(viewDate, jmgGroupCalcId, '');
    }

    if (mode == JmgCalcApproveMode::Approve)
    {
        calcGroup.visible(false);

        // Add approval group range to query.
        rangeGroupApproveId = jmgStampJournalTable_ds.query().dataSourceNo(1).addRange(fieldNum(JmgStampJournalTable,ApproveGroupId));
        rangeGroupApproveId.value(queryValue(jmgGroupApproveId));
        JmgStampJournalTable::makeLines(viewDate, '', jmgGroupApproveId);
    }

    jmgStampJournalTable_ds.executeQuery();
    xSysLastValue::saveLast(this);

    calcGroup.text(jmgGroupCalcId);
    approveGroup.text(jmgGroupApproveId);
}

    /// <summary>
    /// Initializes the class variables of the <c>JmgCalcApproveDateView</c> form
    /// for the form initialization.
