    {
        mode                = _calcApproveParm.calcApproveMode;
		viewDate            = _calcApproveParm.viewDate;
        jmgGroupCalcId      = _calcApproveParm.calcGroupId;
        jmgGroupApproveId   = _calcApproveParm.approveGroupId;
    }

