void modifiedJobRef(JmgStampJournalTrans _jmgStampJournalTrans)
{
    if (!jmgStampJournalTable.FlexProfile && _jmgStampJournalTrans.Module == JmgModuleEnum::IPC && HRMAbsenceCodeGroup::find(_jmgStampJournalTrans.JobRef).JmgAbsenceFlex)
    {
        _jmgStampJournalTrans.JobRef  = '';
        throw error("@SYS40088");
    }

    if ((select count(RecId) from hrmAbsenceCode where hrmAbsenceCode.HrmAbsenceCodeGroupId == _jmgStampJournalTrans.JobRef).RecId == 1)
    {
        _jmgStampJournalTrans.JobId     = (select firstonly HrmAbsenceCodeId from hrmAbsenceCode where hrmAbsenceCode.HrmAbsenceCodeGroupId == _jmgStampJournalTrans.JobRef).JmgJobId;
        _jmgStampJournalTrans.JobIDAbs  = _jmgStampJournalTrans.JobId;
    }
    else
    {
        _jmgStampJournalTrans.JobId     = '';
        _jmgStampJournalTrans.JobIDAbs  = '';
    }
}

void modifiedJourReg(JmgStampJournalTrans _jmgStampJournalTrans)
{
    _jmgStampJournalTrans.JobRef = '';
    _jmgStampJournalTrans.JobId = '';
}

