public void initializeData(JmgProfileDate _newDate)
{
    JmgWorkerRecId          worker;
    JmgStampJournalTable    selectedWorker;

    viewDate    = _newDate;
    worker      = jmgStampJournalTable.Worker;

    rangeInactiveWorkerProfileDates.value(queryValue(_newDate));
    JmgStampJournalTable::makeLines(viewDate,(mode == JmgCalcApproveMode::Approve ? '' : jmgGroupCalcId),(mode == JmgCalcApproveMode::Approve ? jmgGroupApproveId : ''));
    jmgStampJournalTable_ds.executeQuery();

    // Find employee again:
    selectedWorker = JmgStampJournalTable::find(worker, viewDate);
    jmgStampJournalTable_DS.positionToRecord(selectedWorker);
}

