private void switchFilter()
{
    filterError = !filterError;

    jmgStampJournalTable_ds.executeQuery();

    if (filterError == NoYes::Yes)
    {
        filter.text("@SYS38980");
    }
    else
    {
        filter.text("@SYS38981");
    }
}

public boolean unpack(container _packedClass)
{
    int version;
    boolean ret;
    version = conPeek(_packedClass, 1);

    switch (version)
    {
        case #CurrentVersion:
            [version, #CurrentList] = _packedClass;
            ret = true;
            break;
        default:
            ret = false;
            break;
    }

    return ret;
}

