void executeQuery()
{
    element.design().caption((mode == JmgCalcApproveMode::Approve ? "@SYS16449" : "@SYS117") +
                            strFmt("@SYS39863", date2StrUsr(viewDate, DateFlags::FormatAll))+
                            strFmt("@SYS39863",(mode == JmgCalcApproveMode::Approve ? jmgGroupApproveId : jmgGroupCalcId)));

    rangeWorker.enabled(this.enableWorkerRange());
    rangeProfileDate.value(queryValue(viewDate));
    jmgCalculateDate.enabled(viewDate <= DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    jmgApproveDate.enabled(viewDate <= DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    jmgTransferDate.enabled(viewDate <= DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));

    profileDate.dateValue(viewDate);

    if (filterError == NoYes::Yes)
    {
        rangeError.value(queryValue(NoYes::Yes));
    }
    else
    {
        rangeError.value('');
    }

    super();
}

