private Set getSelectedJournals()
{
    Set selectedRecords = new Set(Types::Record);

    JmgStampJournalTable jmgStampJournalTableMarked = JmgStampJournalTable_DS.getFirst(1);
    while (jmgStampJournalTableMarked)
    {
        selectedRecords.add(jmgStampJournalTableMarked);
        jmgStampJournalTableMarked = JmgStampJournalTable_DS.getNext();
    }

    JmgStampJournalTable currentlySelectedJournalRecord = JmgStampJournalTable_DS.cursor() as JmgStampJournalTable;
    if (currentlySelectedJournalRecord && !selectedRecords.in(currentlySelectedJournalRecord))
    {
        selectedRecords.add(currentlySelectedJournalRecord);
    }

    return selectedRecords;
}

