void write()
{
    boolean isInserted;
    ProdTable prodTable;

    JmgStampJournalTrans::changedFromForms(jmgStampJournalTrans);

    select firstonly ProdStatus from prodTable
        where prodTable.ProdId == jmgStampJournalTrans.JobRef;

    if ((jmgStampJournalTrans.Module        == JmgModuleEnum::PROD)
        && ((jmgStampJournalTrans.JobId     != jmgStampJournalTrans.orig().JobId)
            || (jmgStampJournalTrans.JobRef != jmgStampJournalTrans.orig().JobRef))
        && prodTable.status().isAfterOrEqualTo(ProdStatus::Completed))
    {
        warning(strFmt("@SYS22250",jmgStampJournalTrans.JobRef, prodTable.ProdStatus));
    }

    if (!isJmgStampJournalTransProdTransferFlightEnabled)
    {
        isInserted = jmgStampJournalTrans.RecId == 0;
    }

    if (JmgStampJournalTrans.JourRegType != JmgStampJournalTrans.orig().JourRegType ||
        JmgStampJournalTrans.JobId != JmgStampJournalTrans.orig().JobId)
    {
        jmgStampJournalTrans.jourRegChanged();
    }

    if (isJmgStampJournalTransProdTransferFlightEnabled)
    {
        isInserted = jmgStampJournalTrans.isTransactionNewLog();
    }

    super();

    if (isInserted)
    {
        jmgCalcApproveForm.transactionInserted(jmgStampJournalTrans);
    }
    else
    {
        jmgCalcApproveForm.transactionUpdated(jmgStampJournalTrans);
    }

    jmgStampJournalTrans_DS.reread();
    jmgStampJournalTrans_DS.refresh();

    if (jmgStampJournalTrans.StampType == JmgStampTypeEnum::Absence)
    {
        jmgStampJournalTrans_Absence_DS.executeQuery();
    }
}

void executeQuery()
